INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (1, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_1.png', '1. 손질된 새우를 끓는 물에 데쳐 건진다.a', 1);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (2, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_2.png', '2. 연두부, 달걀, 생크림, 설탕에 녹인 무염버터를 믹서에 넣고 간 뒤 새우(1)를 함께 섞어 그릇에 담는다.b', 1);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (3, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00028_3.png', '3. 시금치를 잘게 다져 혼합물 그릇(2)에 뿌리고 찜기에 넣고 중간 불에서 10분 정도 찐다.c', 1);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (4, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054820_1675673300714.jpg', '1. 부추는 깨끗이 씻어 물기를 제거하고, 5cm 길이로 썰고 부추에 날콩가루를 넣고 고루 섞이도록 버무린다.', 2);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (5, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054834_1675673314720.jpg', '2. 찜기에 면보를 깔고 부추를 넣은 후 김이 오르게 쪄서 파랗게 익힌다.', 2);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (6, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/20230206/20230206054908_1675673348152.jpg', '3. 저염간장에 다진 대파, 다진 마늘, 고춧가루, 요리당 , 참기름, 참깨를 섞어 양념장을 만들고 찐 부추는 그릇에 담아낸다.', 2);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (7, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_1.png', '1. 물기를 빼고 2cm 정도의 크기로 썰은 부추와 양파를 양념장에 섞어 양념속을 만든다.', 3);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (8, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_4.png', '2. 깨끗이 씻은 방울토마토는 꼭지를 떼고 윗부분에 칼로 십자모양으로 칼집을 낸다.', 3);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (9, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00031_5.png', '3. 칼집을 낸 방울토마토에 양념속을 사이사이에 넣어 버무린다.', 3);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (10, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_1.png', '1. 사과, 순두부를 믹서에 넣고 곱게 갈아 소스를 만든다.', 4);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (11, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_2.png', '2. 오이는 소금으로 문질러 씻어 반을 갈라 씨를 제거하고 어슷썰기를 한다.', 4);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (12, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00032_3.png', '3. 썰어 놓은 오이에 순두부사과 소스를 넣고 버무린 후 다진 땅콩을 뿌려 마무리 한다.', 4);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (13, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_2.png', '2. 북어채를 잘게 손으로 찢어 찬물에 헹구어 준비한 후 양파, 표고버섯, 사과는 채 썰고 새우는 등쪽의 두세 마디에 꼬챙이를 넣어 내장을 뺀 후 헹군다.', 5);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (14, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_3.png', '3. 찬물에 북어채, 새우, 표고버섯, 양파를 넣고 20분 정도 끓인 후 사과를 넣어 북어의 씁쓸한 맛을 없앤다.', 5);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (15, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00033_5.png', '5. 그릇에 담아낸다.', 5);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (16, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_3.png', '3. 황태는 손질하여 물에 헹궈 건져 놓고 콩나물은 다듬어 씻고 청양고추는 어슷썰기 한다.', 6);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (17, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_4.png', '4. 냄비에 물을 붓고 황태와 무를 넣고 끓인 후 육수에서 물을 건져내고 저염 된장을 푼다.', 6);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (18, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00036_5.png', '5. 콩나물, 다진 마늘, 청양고추를 넣고 뚜껑을 덮어 김이 나게 끓\n여준다.', 6);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (19, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_2.png', '1. 감자, 양파는 얇게 썰고 애느타리 버섯은 썰어 달궈진 팬에 굽는다.', 7);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (20, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_4.png', '2. 냄비에 물을 붓고 된장을 푼 뒤 감자, 양파, 두부를 넣어 재료가 투명해지게 끓인 후 된장국의 재료를 건져서 믹서에 넣어 갈고 된장국에 넣어 한번 더 끓인다.', 7);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (21, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00037_6.png', '3. 구운 애느타리버섯과 대파를 국에 넣어 끓인 후 그릇에 담는다.', 7);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (22, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_1.png', '1. 찬물에 표고버섯,머리와 내장을 제거한 멸치,\n다시마, 양파를 넣어 중간 불에서 10분 정도\n끓여 멸치육수를 만든다.', 8);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (23, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_2.png', '2. 육수의 표고버섯 기둥, 멸치, 다시마, 양파를 건진 후 표고버섯은 편으로 썰고 청경채는 4cm 크기로 썬다.', 8);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (24, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00038_4.png', '3. 끓는 육수에 준비한 표고버섯과 청경채를 넣고 국간장으로 간을 한 후 마늘을 넣어 한소끔 끓인다.', 8);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (25, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_2.png', '1. 올리브유, 식초, 설탕, 다진 마늘을 섞어 거품기로 충분히 저어주어 올리브마늘 드레싱을 만든다.', 9);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (26, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_3.png', '2. 치커리는 싱싱하게 찬물에 담갔다가 물기를 뺀 후 한입 크기로 자르고, 적양배추, 양파, 당근은 곱게채를 썬다.', 9);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (27, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00095_4.png', '3. 접시에 준비한 치커리, 적양배추, 양파, 당근을 담고 올리브마늘 드레싱을 뿌린다.', 9);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (28, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_1.png', '1. 찬물이 담긴 냄비에 식초, 소금을 넣고 메추리알을 삶는다. 물이 끓어오르면 5분 정도 더 삶아 찬물에 헹군 후 껍질을 벗기고 반으로 자른다.', 10);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (29, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_2.png', '2. 딸기를 흐르는 물에 가볍게 씻어 꼭지를 제거한 후 물기를 빼고 반으로 자른다.', 10);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (30, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00087_4.png', '3. 양상추는 찬물에 담갔다가 물기를 빼고 한입 크기로 찢은 후 접시에 양상추, 딸기, 블루베리, 메추리알을 담고 플레인요거트를 끼얹는다.', 10);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (31, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_1.png', '1. 시금치는 끓는 소금물에 데쳐 찬물에 헹구어 물기를 짜고 우유를 넣고 블렌더에 곱게 갈아 체에 거른다.', 11);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (32, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_2.png', '2. 껍질 벗긴 감자는 찜기에 넣어 20분 정도 삶고 꺼내서 으깨고, 아몬드는 잘게 다지며, 치커리는 곱게 다진다.', 11);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (33, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00089_5.png', '3. 으깬 감자, 다진 치커리, 시금치 우유소스, 설탕을 넣고 섞어 접시에 담고 아몬드, 크랜베리를 올리고 시금치우유 소스를 곁들인다.', 11);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (34, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_1.png', '1. 곱게 다진 양파와 오이피클은 물기를 짠다.', 12);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (35, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_2.png', '2. 연두부에 다진 양파, 다진 오이피클, 올리브유, 머스터드, 후추, 꿀, 식초, 레몬즙을 넣어 두부타르타르 소스를 만든다.', 12);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (36, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00091_5.png', '3. 새송이버섯은 0.5cm 두께로 썰고 프라이팬에 노릇하게 구워준 후 접시에 새송이버섯과 치커리를 담고 소스를 곁들인다.', 12);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (37, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_1.png', '1. 저염간장, 식초, 레몬즙, 설탕을 혼합하여 간장레몬 소스를 만든다.', 13);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (38, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_2.png', '2. 호박, 가지, 새송이버섯은 3cm 길이로 자른 후 얇은 편으로 채 썰고, 양파, 파프리카, 피망은 호박 길이로 썬다.', 13);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (39, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00093_3.png', '3. 가지, 호박, 새송이버섯, 양파, 파프리카, 피망에 올리브유를 바르고 달궈진 그릴 팬에 구운 후 접시에 담고 발사믹 크레타를 뿌리고 간장레몬 소스를 곁들인다.', 13);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (40, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_1.png', '1. 두유에 플레인요거트와 레몬즙, 설탕을 넣고 잘 섞어 두유요거트 소스를 만든다.', 14);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (41, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_4.png', '2. 강낭콩은 물에 불린 후 콩과 물을 1:2.5로 넣고 20~25분 삶아 건지고 브로콜리와 컬리플라워는 한입 크기로 썰어 끓는 소금물에 살짝 데쳐 찬물에 헹궈 물기를 뺀다.', 14);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (42, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00094_5.png', '3. 접시에 데친 브로콜리와 컬리플라워, 삶은 강낭콩, 채썬 적양파, 다진 호두, 건포도를 담고 두유요거트 소스를 곁들여 낸다.', 14);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (43, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_1.png', '1. 잣, 아몬드, 해바라기씨, 호두, 호박씨는 다진다.', 15);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (44, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_2.png', '2. 끓는 물에 저염소금과 식초를 넣은 후 소면을 삶은 후 체에 건져 물기를\n뺀다.', 15);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (45, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00111_3.png', '3. 석류는 물 600g와 함께 믹서에 갈고 체에 걸러 즙을 낸다. 그릇에 면을 담고 차갑게 식힌 석류즙을 붓고 견과류, 채 썬 오이를 고명으로 올린다.', 15);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (46, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_1.png', '1. 검은콩을 1시간 찬물에 담가 불린 후 삶아 익힌 후 불순물을 제거하고 흑임자, 생수, 삶은 콩, 호두를 믹서에 갈아 살엄음이 생기도록 냉동실에 넣는다.', 16);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (47, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_4.png', '2. 오이는 돌려 깎아 채 썰고, 토마토는 1/8크기로 썰고, 달걀은 삶아 반으로 자른다.', 16);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (48, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00113_6.png', '3. 실곤약은 끓는 물에 데치고 찬물에 헹궈 그릇에 담은 후 콩물을 담고 오이, 토마토, 달걀을 고명으로 올려 완성한다.', 16);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (49, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_1.png', '1. 브로콜리, 숙주, 표고버섯, 닭 가슴살은 다지고 다진 닭 가슴살은 소금과 후추로 밑간 한다.', 17);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (50, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_3.png', '2. 다진 채소와 닭가슴살, 참기름, 저염간장을 섞어 만두소를 만든다.', 17);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (51, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00114_4.png', '3. 준비한 만두피에 만두소를 넣은 후 만두 모양으로 빚고 만두를 찜통에 넣어 찌고 그릇에 담는다.', 17);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (52, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00131_1.png', '1. 함초, 노루궁뎅이버섯, 다시마, 파, 양파, 무를 물에 넣어 10분 끓이고 향이 강한 모시조개와 냉이를 넣고 30분  끓인 후 간장으로 색을 낸다.', 18);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (53, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00131_4.png', '2. 소면은 따로 삶는다.', 18);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (54, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00131_5.png', '3. 그릇에 삶은 소면을 담은 후 육수를 붓고 지단, 슬라이스한 청고추, 실고추를 올려 완성한다.', 18);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (55, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00137_1.png', '1. 두부에 쌀뜨물을 넣고 갈아준다.', 19);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (56, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00137_2.png', '2. 돼지고기는 썰고 김치는 물에 씻어 양념을 제거하고 3×3cm로 썬다.', 19);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (57, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00137_3.png', '3. 냄비에 돼지고기와 김치를 함께 볶은 후, 망에 거른 된장과 1의 두부, 고춧가루, 청양고추, 홍고추를 넣어 끓이다가 대파를 넣어 완성한다.', 19);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (58, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00138_1.png', '1. 김치, 베이컨, 스팸, 소시지, 양파, 두부, 무는 두께 0.5cm로 썬다.', 20);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (59, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00138_2.png', '2. 다시마와 물을 끓여 다시마물을 만든다.', 20);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (60, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00138_3.png', '3. 냄비에 소시지, 베이컨, 두부, 스팸, 무, 우민찌, 김치, 다시마물 300g을 넣어 끓인 후 저염된장, 양파, 대파, 다진 마늘, 떡국 떡을 넣고 재료가 다 익으면 홍고추와 청양고추를 넣어 완성한다.', 20);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (61, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00181_4.png', '1. 감자는 믹서에 갈고 양배추는 채 썰고 고기, 당근, 청양고추, 두부는 다진다.', 21);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (62, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00181_5.png', '2. 준비해둔 재료를 모두 섞고 부침가루와 계란을 넣어 반죽한다.', 21);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (63, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00181_6.png', '3. 가열된 팬에 기름을 두르고 반죽을 부어 굽고 소스를 함께 곁들인다.', 21);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (64, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00074_1.png', '1. 청양고추와 홍고추를 다져서 참기름에 살짝 볶은 후 볶은 고추에 마요네즈와 레몬즙을 넣어 소스를 만든다.', 22);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (65, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00074_4.png', '2. 돌나물은 손질하여 찬물에 담구고 새송이버섯은 달군 프라이팬에 참기름을 두르고 노릇\n하게 굽는다.', 22);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (66, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00074_5.png', '3. 돌나물은 물기를 빼고 구운 새송이버섯과 레몬마요네즈 소스를 넣고\n버무려 접시에 담는다.', 22);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (67, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00159_1.png', '1. 배추를 소금물에 하루 동안 절인 후 물기를 빼고 석류는 즙을 낸다.', 23);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (68, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00159_3.png', '2. 무는 채 썰고, 쪽파와 미나리는 4cm로 자른다.', 23);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (69, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00159_4.png', '3. 무채, 미나리, 쪽파, 다진 마늘, 다진 생강, 새우젓국을 넣고 섞어 소를 만들고 절인 배추에 소를 넣고 오므려 싼다. 석류즙에 소를 채운 배추를 담가 완성한다.', 23);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (70, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00161_1.png', '1. 발사믹 식초, 화이트와인, 다진 유자청 건더기를 넣고 졸여 소스를 만들고 대구를 손질하여 포를 뜬다.', 24);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (71, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00161_3.png', '2. 대파, 청파프리카, 홍파프리카, 황파프리카를 채 썬다.', 24);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (72, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00161_4.png', '3. 대구포에 밀가루를 묻혀 청파프리카, 홍파프리카, 황파프리카, 황금팽이버섯을 넣고 말아 미나리로 묶어 찐 후 소스를 뿌리고 썬 대파를 올려 완성한다.', 24);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (73, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00162_1.png', '1. 황파프리카를 갈아 체에 내리고 멥쌀로 쌀풀을 만들어 황파프리카 즙을 섞고 물엿을 첨가하여 양념을 만든다.', 25);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (74, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00162_3.png', '2. 배추를 소금물에 절이고 절인 배추를 찬물로 씻어 소금기를 뺀다.', 25);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (75, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00162_5.png', '3. 소금기를 뺀 배추에 채썬 자색고구마, 무, 쪽파, 부추, 사과, 배를 놓고 양념을 부은 후 3일 동안 숙성시켜 완성한다.', 25);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (76, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00193_1.png', '1. 양파와 마늘은 다져서 볶다가 토마토 과육과 설탕, 바질을 넣고 고추장을 섞어 토마토 소스를 만든다.', 26);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (77, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00193_2.png', '2. 가지, 양송이버섯, 애호박, 사과는 썰고 살라미는 데친다.', 26);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (78, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00193_3.png', '3. 오븐용 팬에 토마토 소스를 넓게 펴준 뒤 애호박, 가지, 양송이버섯, 살라미를 겹치게 넣고 180℃로 예열한 오븐에 넣고 30분 구워 완성한다', 26);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (79, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00201_1.png', '1. 간장, 레몬즙, 설탕, 다진 홍고추를 섞어 드레싱을 만든다.', 27);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (80, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00201_2.png', '2. 민들레 잎은 4×4cm로 썰고, 비트는 채 썰고 물에 담가 색을 뺀다.', 27);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (81, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00201_4.png', '3. 오렌지는 씻어 껍질만 발라 채 썰고 민들레 잎, 비트, 오렌지 껍질을 섞어 접시에 담고 드레싱을 곁들인다.', 27);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (82, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_1.png', '1. 끓는 물에 배춧잎을 데친다.', 28);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (83, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_2.png', '2. 당근, 호박, 표고버섯, 양파는\n입자있게 다진다.', 28);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (84, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_3.png', '3. 돼지고기를 썰어 불린 쌀, 다진 야채와\n함께 밥을 짓는다.', 28);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (85, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_4.png', '4. 데친 배춧잎에 지어놓은 밥을 올려\n돌돌만다.', 28);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (86, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_5.png', '5. 말아 놓은 배춧잎을 부추로 묶는다.', 28);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (87, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00636_6.png', '6. 찜통에 김이 오르면 ?번을 넣고 약 5분\n정도 찐다.', 28);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (88, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_1.png', '1. 대추는 돌려 깍아 씨를 제거 하고 곱게\n다진다.', 29);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (89, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_2.png', '2. 다진 소고기와 돼지고기에 대추와 소금\n후춧가루를 넣고 치댄다.', 29);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (90, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_3.png', '3. 애호박과 단호박, 파프리카는\n입자있게 썰어 ?에 넣고 잘 치댄다.', 29);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (91, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_4.png', '4. 양송이는 편으로 썰어 팬에 익히고,\n인삼을 뇌두를 자르고 깨끗이 씻어\n팬에 볶아 잘게 썰어 ?에 넣는다.', 29);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (92, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_5.png', '5. 재료가 골고루 섞인 떡갈비를 갈비\n모양으로 만든다.', 29);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (93, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00637_6.png', '6. 떡갈비를 팬에 굽고, 접시에 배춧잎을\n깔고 익힌 양송이를 올리고 떡갈비를\n담는다.', 29);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (94, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_1.png', '1. 닭을 깨끗이 손질하고, 부위별로\n자른다.', 30);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (95, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_2.png', '2. 냄비에 가시오가피, 대파, 다시마,\n마늘, 건새우를 넣고 은근히 끓인 후\n닭을 넣고 약 20분 정도 삶아 건진 뒤\n살을 찢어둔다.', 30);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (96, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_3.png', '3. 양파, 오이는 채썰고 식초, 설탕,\n소금을 넣고 초절임한다', 30);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (97, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_4.png', '4. 겨자가루에 30℃ 정도의 물을 넣고\n골고루 섞어 발효시키고, 설탕, 식초,\n소금을 넣고 겨자소스를 만든다', 30);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (98, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_5.png', '5. 흰곤약과 비트, 치자가루를 섞은 곤약,\n채썬 다시마를 접시에 담는다.', 30);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (99, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00638_6.png', '6. 양파, 오이 초절임과 닭살을 한쪽에\n담아 먹기 직전에 겨자소스를 올린다.', 30);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (100, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_1.png', '1. 찹쌀은 깨끗이 씻어 30분 정도 불린다', 31);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (101, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_2.png', '2. 불린 찹쌀은 냄비에 넣고 질게 밥을\n한다.', 31);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (102, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_3.png', '3. 닭가슴살은 넓게 펴서 소금,\n후춧가루를 뿌린다.', 31);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (103, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_4.png', '4. 호박잎과 미나리는 끓는 물에 살짝\n데치고, 대추는 돌려 깎아 씨를\n제거하고 인삼은 뇌두를 제거한 뒤\n채를 썰어둔다.', 31);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (104, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_5.png', '5. 데친 호박잎에 닭 가슴살을 올린다.', 31);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (105, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00639_6.png', '6. 닭 가슴살위에 찹쌀밥과 준비한 야채와\n대추를 올려 돌돌 말아 질게 된\n찹쌀밥에 넣고 약 20분 정도 더 쪄낸다.', 31);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (106, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_1.png', '1. 된장에 두부를 넣고 골고루 섞는다.', 32);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (107, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_2.png', '2. 표고버섯과 마늘은 함께 갈아둔다.', 32);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (108, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_3.png', '3. 미역은 충분히 불린다.', 32);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (109, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_4.png', '4. 냄비에 황태채와 갈아둔 표고버섯,\n마늘을 넣고 은근히 끓여 육수를\n만든다.', 32);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (110, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_5.png', '5. 곤약은 납작납작하게 썰어 끓는 물에\n데친다.', 32);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (111, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00640_6.png', '6. 육수에서 황태를 건져 두부된장과 함께\n갈아 육수에 붓고, 미역, 곤약,\n들깨가루를 넣고 약 20분 정도 끓인 후\n액젓으로 간을 한다.', 32);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (112, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_1.png', '1. 훈제오리 가슴살을 슬라이스한다.', 33);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (113, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_2.png', '2. 슬라이스한 훈제오리는 팬에 굽는다.', 33);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (114, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_3.png', '3. 양파는 채썰고 찬물에 담근 뒤 건진다.', 33);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (115, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_4.png', '4. 레디쉬는 채썰고 찬물에 담근 뒤\n건진다.', 33);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (116, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_5.png', '5. 먹기 직전에 발사믹소스를 만든다.', 33);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (117, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00641_6.png', '6. 접시에 훈제오리와 양파채, 레디쉬,\n블루베리를 담고 발사믹소스를\n올린다.', 33);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (118, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_1.png', '1. 훈제오리 가슴살은 올리브오일,\n로즈마리, 후춧가루로\n마리네이드한다.', 34);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (119, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_2.png', '2. 새송이버섯은 편으로 썰고 팬에\n굽는다.', 34);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (120, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_3.png', '3. 오렌지는 슬라이스로 썬다.', 34);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (121, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_4.png', '4. 땅콩과 파슬리는 입자있게 다진다.', 34);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (122, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_5.png', '5. 마리네이드한 오리 고기를 구워 접시에\n담는다.', 34);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (123, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00642_6.png', '6. 유자청에 땅콩, 파슬리가루를 넣고\n소스를 만들고, 구워진 오리고기\n사이에 오렌지 슬라이스를 넣고\n유자청소스를 올린다.', 34);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (124, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_1.png', '1. 전복은 수저로 떼어내어 소금으로 비벼\n깨끗이 씻는다.', 35);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (125, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_2.png', '2. 시금치는 데쳐 다지고, 새우는 껍질을\n벗겨 전복, 해초와 함께 다진다.', 35);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (126, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_3.png', '3. ?번을 잘 섞어 전복 껍질에 넣는다.', 35);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (127, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_4.png', '4. 찜통에 김이 오르면 속을 채운 전복\n껍질을 넣고 약 10분 정도 찐다.', 35);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (128, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_5.png', '5. 저염간장, 레몬, 설탕, 다진마늘을\n넣고 살짝 끓여 소스를 만든다.', 35);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (129, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00643_6.png', '6. 간장소스에 전분을 풀어 농도를\n맞추고, 전복찜에 여러번 바르고\n식용꽃을 올린다.', 35);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (130, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_1.png', '1. 검은콩은 깨끗이 씻어 약 12시간 정도\n충분히 불린다.', 36);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (131, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_2.png', '2. 불린 검은콩은 20분 정도 삶아 깨끗이\n씻는다.', 36);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (132, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_3.png', '3. 삶은 검은콩은 믹서기에 곱게 갈아\n차갑게 식힌다.', 36);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (133, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_4.png', '4. 오이는 씨를 제거하고 깨끗이 씻어\n채썬다.', 36);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (134, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_5.png', '5. 물을 끓여 메밀면을 삶고, 찬물에\n여러번 헹구어 건져 물기를 뺀다.', 36);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (135, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00644_6.png', '6. 메밀면을 그릇에 담고 차가운 콩 국물,\n땅콩, 잣, 오이, 방울토마토 및 통깨를\n올린다.', 36);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (136, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_1.png', '1. 어린잎은 찬물에 담근다.', 37);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (137, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_2.png', '2. 마는 껍질을 벗겨 동글동글하게 썬다', 37);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (138, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_3.png', '3. 썰어 놓은 마는 팬에 기름을 살짝\n두르고 앞뒤로 굽는다.', 37);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (139, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_4.png', '4. 참나물, 돈나물, 토마토, 레디쉬는\n먹기 좋은 크기로 썬다.', 37);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (140, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_5.png', '5. 겨자가루는 30℃ 물을 섞어 발효\n시킨다.', 37);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (141, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00645_6.png', '6. 저염간장에 식초, 설탕, 발효겨자를\n넣고 골고루 섞어 소스를 만들고,\n접시에 구운 마와 손질한 야채를 넣고\n만들어 놓은 소스와 어린잎을 올린다.', 37);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (142, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_1.png', '1. 육수 재료를 넣고 중간 불로 끓인 뒤\n체에 걸러 육수를 만든다.', 38);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (143, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_2.png', '2. 닭가슴살은 채 썬 뒤 물에 넣고\n삶는다.', 38);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (144, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_3.png', '3. 애호박은 반달 썰고, 미나리는 5㎝\n길이로 썰고, 청양고추는 잘게 썬 뒤\n찬물에 담가 씨를 빼고, 두부는 한입\n크기로 썰어 물에 담가둔다.', 38);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (145, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_4.png', '4. 김치를 한입 크기로 썬 뒤 참기름에\n볶다가 육수를 붓는다.', 38);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (146, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00271_5.png', '5. 두부, 애호박, 느타리버섯, 미나리,\n닭가슴살을 넣어 끓어오르면\n달걀물과 청양고추를 넣고 조금\n더 끓여 마무리한다.', 38);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (147, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_1.png', '1. 대파, 마늘, 쇠고기를 물(300g)과\n함께 끓인 뒤 면포에 걸러 육수를\n만든다.', 39);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (148, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_2.png', '2. 콩나물, 김치를 물에 씻어 손질한다.', 39);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (149, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_3.png', '3. 두부는 물기를 빼고, 붉은 고추는\n다진 뒤 돼지고기와 섞어 반죽해\n동그랗게 빚어 완자를 만든다.', 39);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (150, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_4.png', '4. 김 오른 찜기에 완자를 넣어 찐다.', 39);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (151, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_5.png', '5. 육수를 끓이고 끓어오르면 김치,\n콩나물, 새우젓을 넣는다.', 39);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (152, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00272_6.png', '6. 한소끔 더 끓이다가 완자를 넣고\n조금 더 끓여 마무리한다.', 39);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (153, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_1.png', '1. 풋고추는 잘게 다진다.', 40);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (154, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_2.png', '2. 백김치는 먹기 좋은 크기로 썬다.', 40);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (155, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_3.png', '3. 냄비에 백김치를 볶다가 김치가\n익으면 참기름, 후춧가루,\n다진 마늘을 넣고 볶는다.', 40);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (156, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_4.png', '4. 물(200g)을 넣고 끓인다.', 40);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (157, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00273_5.png', '5. 물이 끓으면 콩비지와 간장,\n풋고추를 넣어 약한 불에서 15분\n정도 끓여 마무리한다.', 40);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (158, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_1.png', '1. 육수 재료를 넣고 끓인 뒤 체로 걸러\n육수를 낸다.', 41);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (159, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_2.png', '2. 오징어 몸통은 큼지막하게 썰고\n다리는 얇게 썬다.', 41);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (160, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_3.png', '3. 두부와 버섯은 먹기 좋게 썰고,\n고추는 어슷하게 썰고, 고구마는\n먹기 좋은 크기로 썰고, 콩나물은\n다듬는다.', 41);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (161, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_4.png', '4. 김치는 먹기 좋은 크기로 썬 뒤\n냄비에 참기름을 두르고 볶다가\n고구마, 콩나물, 육수를 넣고 끓인다.', 41);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (162, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_5.png', '5. 자작하게 끓으면 오징어를 넣고\n끓이고, 오징어가 익으면 들깻가루를\n넣고 끓인다.', 41);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (163, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00274_6.png', '6. 두부, 팽이버섯, 쑥갓, 고구마,\n붉은 고추를 넣고 끓여 마무리한다.', 41);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (164, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306022942_1678080582607.jpg', '1. 냄비에 육수 재료를 넣고 끓이다가 물이 끓어오르면 다시마를 건지고 조금 더 끓여 육수를 우려낸다.', 42);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (165, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023039_1678080639676.jpg', '2. 주꾸미는 내장과 입, 눈을 제거하고 4cm 크기로 잘라 준비한다.', 42);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (166, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023103_1678080663820.jpg', '3. 김치는 국물을 꽉 짜서 한입 크기로 썰고, 무는 편 썰고, 마늘은 다지고, 대파는 어슷 썰고, 양파는 굵게 채 썰고, 두부는 납작하게 썬다.', 42);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (167, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/20230313/20230313110637_1678673197807.jpg', '4. 냄비에 참기름을 두르고, 김치, 무, 양파가 투명해질 때까지 볶다가 육수를 붓고 끓으면 주꾸미, 다진 마늘을 넣어 더 끓인다.', 42);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (168, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023149_1678080709863.jpg', '5. 김칫국물과 배즙을 2:1로 섞은 뒤 찌개에 넣어 간을 맞춘다.', 42);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (169, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306023210_1678080730863.jpg', '6. 두부, 팽이버섯을 넣고 한소끔 끓여 마무리한다', 42);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (170, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_1.png', '1. 다시마육수재료를 물에 넣고 끓여\n육수를 낸다.', 43);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (171, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_2.png', '2. 느타리버섯은 가닥가닥 떼고, 두부는\n썰고, 콩나물은 머리와 꼬리를 떼\n손질한다.', 43);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (172, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_3.png', '3. 배추와 미나리를 데친다.', 43);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (173, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_4.png', '4. 돼지고기, 쇠고기에 고기완자양념을\n넣어 반죽한 뒤 빚어 완자를 만든다.', 43);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (174, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00276_5.png', '5. 냄비에 모든 재료를 돌려 담은 뒤\n육수를 부어 끓인다.', 43);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (175, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_1.png', '1. 통조림 햄은 한 번 데친 뒤 우유에\n담가두고, 실곤약은 식초를 넣은\n끓는 물에 살짝 데친다.', 44);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (176, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_2.png', '2. 양파는 굵게 채 썰고, 느타리버섯은\n찢고, 표고버섯은 모양대로 썰고,\n팽이버섯은 끝부분을 잘라내고,\n청양고추와 붉은 고추는 어슷 썬다.', 44);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (177, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_3.png', '3. 감자, 무, 두부는 0.5㎝ 두께로 썰고,\n애호박은 반달썰기 하고 깻잎은\n채 썰어 찬물에 담가 매운맛을\n제거한다.', 44);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (178, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_4.png', '4. 사골육수에 다시마를 넣어 끓인 후\n한 김 식힌다.', 44);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (179, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_5.png', '5. 다진 돼지고기에 다진 파, 다진 마늘,\n후춧가루, 소금을 섞어 완자를\n만들고 밀가루와 달걀물을 묻혀\n식용유(5g)를 두른 팬에 부친다.', 44);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (180, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00277_6.png', '6. 냄비에 재료를 보기 좋게 담고\n사골육수를 부은 뒤 끓이다가\n쑥갓과 들깻가루를 넣어 마무리한다.', 44);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (181, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_1.png', '1. 육수 재료를 넣고 20분간 끓여\n육수를 낸다.', 45);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (182, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_2.png', '2. 닭가슴살과 당근을 다져 모양을\n잡은 뒤 김 오른 찜기에 넣고 찐다.', 45);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (183, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_3.png', '3. 고추, 대파는 어슷 썰고, 쑥갓,\n표고버섯, 애호박, 김치는 먹기\n좋은 크기로 썰고, 팽이버섯은\n밑동을 잘라 손질한다.', 45);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (184, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_4.png', '4. 당면과 숙주는 뜨거운 물에 데쳐\n준비한다.', 45);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (185, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_5.png', '5. 양념장 재료를 섞은 뒤 육수(13g)를\n넣고 양념장을 만든다.', 45);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (186, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00278_6.png', '6. 준비한 재료를 담고 육수를 부은 뒤\n양념장을 넣고 끓여 마무리한다.', 45);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (187, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012641_1678249601913.jpg', '1. 통조림 햄을 썰어 끓는 물에 데친다.', 46);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (188, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012702_1678249622285.jpg', '2. 애호박은 반달모양으로 썰고, 두부는 도톰하게 썰고, 팽이버섯은 3등분하고, 청양고추는 어슷 썬다.', 46);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (189, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012717_1678249637881.jpg', '3. 김치는 씻어서 잘게 썬다.', 46);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (190, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012732_1678249652118.jpg', '4. 양념장을 만든다.', 46);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (191, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012747_1678249667092.jpg', '5. 냄비에 물과 다시마를 넣고 살짝 끓여 육수를 만든다.', 46);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (192, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308012805_1678249685798.jpg', '6. 육수에 손질한 재료를 담고 양념장의 1/2 분량을 넣고 중간 불에 끓이다가 쑥갓과 남은 양념장을 넣고 약한 불에 조금 더 끓여 마무리한다.', 46);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (193, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_1.png', '1. 굴은 깨끗이 씻은 뒤 김 오른 찜기에\n찐다.', 47);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (194, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_2.png', '2. 굴을 쪄낸 물(250g)에 멸치,\n표고버섯 밑동을 넣고 끓인 뒤\n멸치와 표고버섯 밑동을 건져내\n육수를 만든다.', 47);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (195, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_3.png', '3. 느타리버섯은 밑동을 제거한 후\n씻어 세로로 찢고, 표고버섯은\n0.5㎝ 두께로 썬다.', 47);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (196, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_4.png', '4. 무는 나박 썰고 양파는 채 썰고,\n미나리는 3㎝ 길이로 썰고,\n대파와 고추는 송송 썬다.', 47);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (197, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_5.png', '5. 육수에 굴, 다진 마늘, 무, 양파,\n버섯을 넣고 끓인다.', 47);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (198, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00280_6.png', '6. 들깻가루와 순두부를 넣고 한소끔\n끓인 뒤 국간장과 소금으로 간을\n맞추고 대파, 붉은 고추, 미나리를\n올려 마무리한다.', 47);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (199, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_1.png', '1. 냄비에 육수 재료를 넣어 끓인다.', 48);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (200, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_2.png', '2. 콩을 충분히 불려 믹서기에 물을\n넣고 갈아 준비한다.', 48);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (201, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_3.png', '3. 느타리버섯은 찢고, 표고버섯은 먹기\n좋은 크기로 자르고, 애호박과\n양파는 깍둑 썰고, 대파와 고추는\n송송 썬다.', 48);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (202, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_4.png', '4. 바지락은 해감해 준비한다.', 48);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (203, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_5.png', '5. 육수에 콩, 느타리버섯, 표고버섯,\n애호박, 저염된장, 새우, 바지락을\n넣고 끊인다.', 48);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (204, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00281_6.png', '6. 순두부를 넣고 끊인 후 양파, 대파,\n붉은 고추, 풋고추를 넣고 한소끔\n끓여 마무리한다.', 48);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (205, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053656_1678264616396.jpg', '1. 표고버섯은 머리와 밑동을 분리하고, 머리 부분은 잘게 썬다.', 49);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (206, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053712_1678264632312.jpg', '2. 애호박, 감자, 양파는 한입 크기로 썰고, 미나리와 부추는 3cm 길이로 썰고, 대파와 청양고추는 송송 썬다.', 49);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (207, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053727_1678264647414.jpg', '3. 물에 다시마, 표고버섯 밑동을 넣고 끓으면 건더기를 건져 육수를 만든다.', 49);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (208, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053743_1678264663037.jpg', '4. 육수에 저염된장과 찹쌀가루를 풀어 한소끔 끓이고, 감자와 다진마늘을 넣어 끓인다.', 49);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (209, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053800_1678264680719.jpg', '5. 애호박과 표고버섯을 넣고, 순두부, 양파, 들깻가루, 저염국간장을 넣어 한소끔 끓인다.', 49);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (210, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/20230308/20230308053819_1678264699729.jpg', '6. 대파, 청양고추, 미나리, 부추를 듬뿍 올려 마무리한다.', 49);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (211, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_1.png', '1. 토란은 2×2㎝로 썬다.', 50);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (212, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_2.png', '2. 표고버섯은 채 썰고, 고추는\n어슷하게 썬다.', 50);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (213, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_3.png', '3. 냄비에 물(500ml)을 넣고 물이\n끓으면 백합을 넣고 끓여 육수를\n낸다.', 50);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (214, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_4.png', '4. 조개가 입을 벌리면 토란과\n들깻가루, 표고버섯을 넣고 끓인다.', 50);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (215, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_5.png', '5. 육수가 어느 정도 걸쭉해지면\n순두부를 넣고 끓인다.', 50);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (216, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00283_6.png', '6. 붉은 고추와 청양고추를 올려\n마무리한다.', 50);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (217, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_01.png', '1. 토마토홀과 토마토는 송송 다져주고, 마늘,\n양파를 냄비에서 볶은 후 믹서에 갈아 토마토\n스프를 만들어준다.', 51);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (218, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_02.png', '2. 만들어진 스프를 볶아서 신맛을 날려준다.', 51);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (219, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_03.png', '3. 먹물파스타면은 삶은 뒤 오일에 버무려주고\n볶아준다.', 51);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (220, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_04.png', '4. 양파, 애호박, 가지, 2가지색 파프리카를 먹기\n좋게 잘라 팬에 볶아준다.', 51);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (221, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_05.png', '5. 접시에 볶아진 채소와 파스타면을 서로 다시\n볶아준다.', 51);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (222, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00385_06.png', '6. 치즈를 곱게 다져주고 접시에 파스타면을\n올린 뒤, 토마토스프와 치즈가루를 뿌려 완성\n한다.', 51);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (223, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_01.png', '1. 양파, 당근, 오이는 채 썰어준다.', 52);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (224, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_02.png', '2. 열무 물김치와 탄산수, 어간장을 넣어 국수\n국물을 만들어준다.', 52);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (225, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_03.png', '3. 달걀은 삶아준다.', 52);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (226, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_04.png', '4. 실곤약은 삶을 때 레몬즙을 넣어서 곤약의\n특이한 냄새를 제거한다.', 52);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (227, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_05.png', '5. 물기를 제거한 곤약에 어간장을 넣어 밑간을\n해준다.', 52);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (228, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00386_06.png', '6. 그릇에 담고 국물을 부어주고, 채소와 달걀을\n곁들여 완성한다.', 52);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (229, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_01.png', '1. 오징어는 솔방울 모양으로 잘라준다.', 53);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (230, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_02.png', '2. 미니 파프리카, 양파, 청홍고추는 적당한\n크기로 잘라준다.', 53);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (231, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_03.png', '3. 다진마늘을 볶다가 오징어를 넣어 볶는다.', 53);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (232, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_04.png', '4. 홀토마토와 양념장 재료를 넣어 볶는다.', 53);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (233, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_05.png', '5. 팬을 약간 태우는 듯 해서 불맛과 매운맛을\n더해준다.', 53);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (234, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00387_06.png', '6. 접시에 담아주고 볶은 현미를 뿌려 완성한다.', 53);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (235, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_01.png', '1. 쇠고기는 칼집을 살짝 넣어준 후 건 바질을\n뿌려 1시간 정도 숙성시킨다.', 54);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (236, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_02.png', '2. 감자는 껍질을 벗긴 후 먹기 좋게 잘라 뜨거운\n물에 70% 정도 익혀준다.', 54);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (237, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_03.png', '3. 익혀준 감자를 버터를 살짝 바른 후 팬에\n파슬리가루를 뿌려 구워준다.', 54);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (238, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_04.png', '4. 시금치는 데쳐내 물기를 제거하고 다진 양파와\n함께 버터에 볶아준다.', 54);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (239, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_05.png', '5. 사과는 사방 1cm 크기로 자른 후 레몬즙을\n살짝 뿌려준 후 버터에 볶아 소스를 만들어\n준다.', 54);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (240, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00389_06.png', '6. 숙성된 소고기를 팬에 구워주고 곁들이 채소와\n소스를 완성한다.', 54);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (241, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_01.png', '1. 오징어는 내장을 빼서 흐르는 물에 깨끗이\n씻은 후, 다리는 송송 다져주고 레몬즙을\n살짝 뿌려준다.', 55);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (242, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_02.png', '2. 강낭콩은 먼저 삶아준다.', 55);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (243, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_03.png', '3. 피망, 당근, 양파, 대파, 파프리카는 송송\n다져준다.', 55);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (244, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_04.png', '4. 잘라준 오징어다리는 다진마늘과 살짝\n볶아준다.', 55);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (245, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_05.png', '5. 볼에 귀리밥을 넣고 카레가루를 넣고 모든\n재료를 섞어 소 재료를 만든다.', 55);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (246, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00390_06.png', '6. 오징어 안쪽에 밀가루를 묻혀주고 소를 채워\n팬에 구워준다.', 55);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (247, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_01.png', '1. 소고기는 사방 2cm 정도의 크기로 잘라서\n물에서 80% 정도까지 익혀준 후 냉수에\n깨끗하게 세척해 준다.', 56);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (248, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_02.png', '2. 양파는 먹기 좋게 자르고 물 400g과 함께\n간장 물을 만들어준다.', 56);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (249, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_03.png', '3. 간장 물에 데쳐 놓은 소고기부터 졸여주기\n시작한다.', 56);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (250, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_04.png', '4. 메추리알을 넣고 함께 졸여준다.', 56);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (251, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00391_05.png', '5. 방울토마토, 양파, 마늘, 꽈리고추까지 넣고\n마지막으로 졸여준다.', 56);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (252, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_01.png', '1. 당근, 양파, 샐러리는 사방 2cm 정도로\n잘라준다.', 57);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (253, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_02.png', '2. 레몬은 껍질을 아주 곱게 채 썰어준다.', 57);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (254, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_03.png', '3. 소고기는 사방 3cm 정도로 자르고 흰 후추를\n뿌려준다.', 57);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (255, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_04.png', '4. 홀토마토는 먹기 좋게 부수어 준 후 볶아준다.', 57);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (256, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_05.png', '5. 팬에 버터를 넣고 소고기부터 볶기 시작하다\n채소를 넣고 볶아준다. 이 때 두꺼운 채소부터\n볶아주는 것이 고기랑 익는 시간이 같아진다.', 57);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (257, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00392_06.png', '6. 마지막에 다져 놓은 홀토마토를 넣고 함초소금\n으로 간을 한 후 레몬껍질을 올려서 마무리\n한다.', 57);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (258, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_01.png', '1. 소스 재료를 이용해 맛 간장으로 만든 데리\n야끼소스를 만들어준다.', 58);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (259, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_02.png', '2. 참치는 손질해 수분을 빼준 후 흰 후추로\n밑간을 해준다.', 58);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (260, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_03.png', '3. 생강은 아주 곱게 채 썰어 냉수에 헹군다.', 58);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (261, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_04.png', '4. 대파도 아주 가늘게 채 썰어서 냉수에 담가\n매운맛을 빼준다.', 58);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (262, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_05.png', '5. 버터를 올린 팬에 삼치를 약불에서 구워준다.', 58);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (263, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00393_06.png', '6. 구워진 참치를 소스에 졸여주고 대파채를\n올린다. 삼치에 소스를 뿌리고 생강채를 올려\n완성한다.', 58);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (264, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_01.png', '1. 삼겹살은 길이로 잘라서 마늘기름, 통후추,\n로즈마리에 마리네이드 해준다.', 59);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (265, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_02.png', '2. 대파는 흰 부분만 잘라 구워주고, 꽈리고추도\n함께 구워준다.', 59);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (266, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_03.png', '3. 대파는 가늘게 채 썰어 물에 담가준다.', 59);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (267, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_04.png', '4. 물에 담가진 대파를 물기를 빼고 양념해준다.', 59);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (268, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00394_05.png', '5. 삼겹살을 꼬치에 끼워 굽고 구워진 채소,\n대파채와 함께 완성한다.', 59);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (269, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_01.png', '1. 저염 된장을 물 300cc에 풀어준 뒤 건새우를\n담가 30분 정도 담가준 뒤 살짝 끓여준다.', 60);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (270, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_02.png', '2. 새우를 풀어준 된장 물을 체에 걸러준다.', 60);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (271, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_03.png', '3. 아욱은 뜨거운 물에 데쳐서 먹기 좋게 썬다.', 60);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (272, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_04.png', '4. 냄비에 밥과 된장 물을 풀어 끓인다.', 60);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (273, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_05.png', '5. 어느 정도 끓으면 칵테일새우와 건져 놓았던\n새우를 다시 넣어준다.', 60);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (274, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00395_06.png', '6. 불을 끄기 전에 아욱을 넣어 한 번 더 끓여준\n후 완성한다.', 60);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (275, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_01.png', '1. 감자를 슬라이스 하여 찜통에 찐 후 체에\n으깬다. 깻잎을 잘게 채 썰어서 냉수에 담가\n향을 빼준다.', 61);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (276, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_02.png', '2. 베이컨을 한 번 데쳐내 송송 썰어준 후 다진\n마늘과 볶아준다.', 61);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (277, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_03.png', '3. 으깬 감자와 깻잎을 다져서 반죽을 만들고\n엄지손가락 크기로 떼어 밀가루를 뿌리고,\n둥글게 빚은 후 포크로 자국을 내 뇨끼를 만든다.', 61);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (278, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_04.png', '4. 만들어진 뇨끼를 삶아 건져준다.', 61);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (279, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_05.png', '5. 팬에 버터를 넣고 다진양파, 다진마늘, 다진\n대파, 볶은 베이컨, 새송이, 흰후추를 볶다가\n우유와 생크림, 설탕을 넣고 끓여준다.', 61);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (280, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00396_06.png', '6. 뇨끼를 넣어 한 번 더 끓여준 뒤 완성한다.', 61);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (281, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_01.png', '1. 단호박은 껍질을 까고 작게 잘라주고, 양파도\n작게 잘라준다.', 62);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (282, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_02.png', '2. 브로콜리는 뜨거운 물에 데쳐내 준 후 송송\n잘라준다.', 62);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (283, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_03.png', '3. 찹쌀가루는 물 100g에 풀어준다.', 62);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (284, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_04.png', '4. 단호박과 양파는 물 300g에 삶아준다.', 62);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (285, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_05.png', '5. 단호박과 양파가 익으면 믹서에 갈아서 다시\n끓여준 뒤 찹쌀가루물을 넣어 끓여준다.', 62);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (286, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00397_06.png', '6. 호박죽이 끓어오르면 치즈가루를 넣어준 후\n볶은 현미랑 완성한다.', 62);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (287, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_01.png', '1. 팽이버섯을 송송 다진 후 라이스버거 재료로\n라이스버거를 만들어준다.', 63);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (288, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_02.png', '2. 만들어준 버거는 팬에 구워준다.', 63);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (289, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_03.png', '3. 토마토와 미나리를 먹기 좋게 잘라서 겉절이를\n만들어준다.', 63);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (290, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_04.png', '4. 떡갈비 재료로 떡 갈비를 만들어준다.', 63);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (291, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_05.png', '5. 만들어진 떡갈비를 구워준다.', 63);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (292, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00398_06.png', '6. 접시에 토마토 겉절이를 담고 라이스버거와\n떡갈비를 올려 완성한다.', 63);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (293, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052119_1678090879888.jpg', '1. 오이고추는 배를 갈라 씨를 제거하고 세척 후 절임물에 절여준다.', 64);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (294, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052136_1678090896972.jpg', '2. 영양부추, 연근은 송송 다지고 무는 채 썬다.', 64);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (295, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052154_1678090914303.jpg', '3. 손질한 부추와 연근, 무에 절임물을 조금 넣고 절인다.', 64);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (296, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052208_1678090928991.jpg', '4. 고춧가루, 생강청, 통깨를 절임채소에 섞어 소를 만든다.', 64);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (297, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052224_1678090944885.jpg', '5. 오이고추 속에 소를 채워 넣는다.', 64);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (298, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/20230306/20230306052240_1678090960385.jpg', '6. 오이고추를 그릇에 담고 남은 물을 부어 마무리한다.', 64);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (299, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_1.png', '1. 닭은 포를 떠서 생강즙과 정종을 섞어\n발라 놓는다.', 65);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (300, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_2.png', '2. 시금치는 살짝 데쳐 썰고, 당근, 양파,\n샐러리, 파프리카는 약 6Cm 길이로\n채를 썰어 절반은 닭고기말이에 사용\n하고, 절반은 곁들임채소로 사용한다.', 65);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (301, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_3.png', '3. 느타리는 끓는 물에 소금을 넣고 데쳐\n손으로 찢어놓고, 포를 뜬 닭가슴살에\n느타리와 썰어 놓은 채소를 넣어 말아\n주고, 육수를 끓여서 말아놓은 닭을\n넣고 익혀낸다.', 65);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (302, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_4.png', '4. 주꾸미와 새우는 끓는 물에 살짝 데치고,\n청경채는 반으로 썰어 살짝 데쳐 준비\n한다.', 65);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (303, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_5.png', '5. 튀김기름 온도가 180℃ 정도 되면\n누룽지를 넣고 튀겨 기름을 밭쳐 놓는다.', 65);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (304, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00500_6.png', '6. 닭을 넣은 육수(③)에 간장과 녹말,\n마늘, 소금, 후춧가루를 넣고 끓이다가,\n곁들임채소로 남겨 놓은 채소(②)와\n데쳐 놓은 해물 및 청경채를 넣고 한 번 더\n끓인 뒤 튀겨진 누룽지를 넣어 완성한다.', 65);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (305, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_1.png', '1. 광어는 포를 길게 떠서 소금과\n후춧가루에 밑간을 해 놓는다.', 66);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (306, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_2.png', '2. 아몬드와 호두는 입자있게 다져\n준비한다.', 66);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (307, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_3.png', '3. 밑간이 되어 있는 광어포에 밀가루와\n달걀물을 입힌다.', 66);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (308, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_4.png', '4. 달걀물을 입힌 광어살에 다시 다져\n놓은 견과류를 입혀 준다.', 66);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (309, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_5.png', '5. 팬에 올리브 오일을 넣고, 팬이 따끈해\n지면 광어 살을 조심스럽게 앞뒤로\n익혀 접시에 담는다.', 66);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (310, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00501_6.png', '6. 플레인요거트에 꿀과 레몬즙을 넣고\n골고루 섞어 광어스테이크소스를\n만들어 완성한다.', 66);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (311, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_1.png', '1. 두부는 소창에 넣고, 짜서 물기를\n제거하고, 으깨어 놓는다.', 67);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (312, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_2.png', '2. 으깬 두부에 소금과 코코넛 밀크를\n넣고 골고루 섞는다.', 67);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (313, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_3.png', '3. 섞여진 두부를 김이 오른 찜통에 넣고\n약 5분 정도 찐다.', 67);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (314, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_4.png', '4. 당근과 양파는 사각지게 썰고,\n브로컬리는 데쳐 당근 크기로 썰어\n놓는다.', 67);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (315, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_5.png', '5. 돼지고기와 감자는 당근 크기로 썰어\n주고, 냄비에 버터를 녹여 돼지고기를\n먼저 볶다가 양파와 당근, 감자,\n브로컬리를 넣고 다시 볶아 준 뒤,\n쪄놓은 두부를 넣어 한 번 더 볶아준다.', 67);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (316, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00503_6.png', '6. 볶아지는 냄비(⑤)에 카레 가루를 넣고\n살짝 더 볶아준 뒤, 물을 넣고 자작하게\n끓여 완성한다.', 67);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (317, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_1.png', '1. 쌀은 불려 놓고, 마늘은 작게 다지고,\n양파는 새송이와 파프리카와 함께\n입자를 크게 다져 준비한다.', 68);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (318, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_2.png', '2. 소고기는 포를 넓게 떠서 소금과\n후춧가루를 뿌려 밑간을 해 놓는다.', 68);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (319, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_3.png', '3. 냄비에 버터를 넣고, 마늘과 양파를\n먼저 볶다가, 새송이와 파프리카를\n넣는다.', 68);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (320, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_4.png', '4. ③번에 다시 불린 쌀과 강화를 넣고,\n볶다가 불을 줄여, 쌀이 익을 때까지\n익혀 준다.', 68);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (321, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_5.png', '5. 쌀이 익으면, 밑간이 되어 있는\n소고기에 넣고 돌돌 말아 준비한다.', 68);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (322, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00504_6.png', '6. 하얀 된장에 생크림을 넣어 골고루 섞고,\n냄비에 육수를 넣고 생크림을 섞은 하얀\n된장을 넣어 끓으면, 말아놓은 소고기를\n넣고 은근히 졸여 완성한다.', 68);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (323, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_1.png', '1. 닭고기살은 작게 썰어 소금과 후춧\n가루를 살짝 뿌려 밑간을 하고 달걀을\n넣어 섞는다.', 69);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (324, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_2.png', '2. 달걀을 넣어 섞은 닭고기 살에 녹말\n가루를 넣어 골고루 묻힌다.', 69);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (325, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_3.png', '3. 국수는 각각 끓는 물에 살짝 데쳐 물기를\n빼놓는다.', 69);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (326, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_4.png', '4. 물기가 빠진 국수는 튀김기름 170℃\n온도에서 각각 작은 채에 그대로 살짝\n튀겨 둥지를 만든 후 기름을 냅킨에\n빼 놓는다.', 69);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (327, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_5.png', '5. 녹말가루를 묻힌 닭고기살도 튀김\n기름에 튀겨 기름을 빼 놓은 둥지국수\n위에 올린다.', 69);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (328, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00505_6.png', '6. 냄비에 레몬과 설탕을 넣고 물을 넣어\n끓이다가 레몬은 건져 내고, 마늘과\n생강, 연유, 식초, 녹말을 풀어 넣고\n살짝 한 번 더 끓여 녹말소스를 만들어,\n튀겨진 닭고기 위에 올려 완성한다.', 69);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (329, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_1.png', '1. 새우는 꼬리를 자르고 내장을 제거\n한다.', 70);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (330, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_2.png', '2. 스테이크 소고기는 소금과 로즈마리를\n뿌리고 후춧가루를 살짝 뿌려 놓는다.', 70);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (331, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_3.png', '3. 아스파라거스는 팬에 기름 없이\n굽는다.', 70);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (332, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_4.png', '4. 새송이와 가지는 길게 썰어 브로컬리와\n함께 기름 없이 팬에 살짝 구워 놓는다.', 70);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (333, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_5.png', '5. 팬에 버터를 살짝 넣고 새우를 굽고,\n스테이크를 구워 접시에 담는다.', 70);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (334, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00506_6.png', '6. 팬에 와인과 설탕을 넣고 끓이다가,\n당근, 양파와 토마토 마늘, 대파를 다져\n넣고 볶듯이 끓이다가 발사믹소스를\n넣고 한 번 더 졸인 소스를 스테이크\n위에 얹고, 브로컬리와 새송이, 가지,\n아스파라거스를 함께 놓아 완성한다.', 70);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (335, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_1.png', '1. 통 삼겹살에 함초와 소금, 후춧가루를\n뿌려 숙성을 시킨다.', 71);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (336, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_2.png', '2. 부추는 약 6cm 길이로 썰고, 양파는\n채를 썰어 놓고, 당근은 납작하게\n편으로 썰어 준비한다.', 71);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (337, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_3.png', '3. 연두부는 잘게 다져 놓는다.', 71);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (338, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_4.png', '4. 유자청에 쌈장과 설탕, 생크림을 넣어\n골고루 섞는다.', 71);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (339, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_5.png', '5. 골고루 섞은 유자청에 다져 놓은\n연두부를 넣어 섞어 냄비에 넣고, 썰어\n놓은 양파와 당근을 넣어 은근히 끓여\n소스를 만들어 놓는다.', 71);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (340, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00507_6.png', '6. 숙성시킨 통삼겹은 버터를 녹인 팬에\n살짝 구워 다시 오븐에서 20분 정도\n익힌 후 먹기 좋게 썰은 뒤, 접시에\n부추를 깔고 그 위에 구워진 삼겹을\n올리고 소스를 얹어 완성한다.', 71);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (341, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_1.png', '1. 돼지고기와 소고기를 섞어, 매실액을\n넣고 30분 정도 냉장고에서 숙성을\n시킨다.', 72);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (342, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_2.png', '2. 숙성된 돼지고기와 소고기에 다진마늘,\n로즈마리, 빵가루, 후춧가루를 넣고\n골고루 섞는다.', 72);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (343, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_3.png', '3. 파프리카는 반으로 잘라 그 안에 씨를\n수저를 이용해서 파낸다.', 72);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (344, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_4.png', '4. 속을 파낸 파프리카에 양념한 고기를\n넣고, 꼭꼭 눌러 두툼하게 잘라 구워\n접시에 담는다.', 72);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (345, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_5.png', '5. 양파는 입자있게 다져 팬에 기름을\n넣고 은근히 볶아 양파 향을 낸다.', 72);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (346, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00508_6.png', '6. 볶아진 팬(⑤)에 준비한 플레인요거트와\n설탕, 매실액을 넣고 끓이다가, 식초와\n녹말을 넣고 한 번 더 끓여 소스를\n완성한다. 햄버거스테이트(④)에 소스를\n얹고, 어린잎을 올려 완성한다.', 72);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (347, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_1.png', '1. 삼치는 반으로 잘라지지 않게 편으로\n떠서 소금을 살짝 뿌려 잠시 말려\n놓는다.', 73);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (348, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_2.png', '2. 손질을 한 삼치에 녹말가루를 입혀\n팬에 기름을 두르고 구워 접시에 담아\n놓는다.', 73);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (349, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_3.png', '3. 생강은 잘게 썰어, 물에 섞은 후\n거름종이에 걸러 생강즙을 만든다.', 73);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (350, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_4.png', '4. 유자청에 만들어 놓은 생강즙을 넣고\n섞는다.', 73);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (351, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_5.png', '5. 냄비에 레몬을 넣고, 물을 넣고 은근히\n끓인다.', 73);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (352, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00509_6.png', '6. 레몬을 끓인 물에 ④번을 넣고 졸여\n소스를 만든 후 접시에 담아 놓은 삼치\n위에 소스로 올려 완성한다.', 73);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (353, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_1.png', '1. 마늘은 곱게 다지고 양파, 당근,\n방울토마토는 입자있게 다진다.', 74);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (354, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_2.png', '2. 토마토는 씨와 껍질을 제거하고 채를\n썰어놓는다.', 74);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (355, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_3.png', '3. 브로컬리를 끓는 물에 데쳐 찬물에\n헹구어 작게 썰어 준비한다.', 74);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (356, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_4.png', '4. 펜네는 끓는 물에 약 10분 정도\n삶아 건진다.', 74);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (357, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_5.png', '5. 건져진 펜네는 올리브오일을 넣어\n무친 후 그릇에 담아놓는다.', 74);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (358, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00510_6.png', '6. 냄비에 오일을 넣고 마늘과 양파를\n볶다가 당근과 방울토마토를 넣어\n볶으면서 육수를 붓고 설탕과 소금,\n레몬즙, 월계수잎을 넣고 끓인 뒤\n월계수잎을 건져내고, 차갑게 식힌 후\n그릇에 담겨 있는 펜네에 토마토채(②)와\n브로컬리(③)를 함께 넣어 담아 완성\n한다.', 74);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (359, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_1.png', '1. 닭고기살은 끓는 물에 데쳐 손으로\n찢고, 느타리버섯도 데쳐 손으로 찢어\n준비한다.', 75);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (360, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_2.png', '2. 사과는 씨를 제거하고 약 6cm 길이로\n채를 썰고, 오이와 파프리카도 같은\n길이로 채를 썰어 놓는다.', 75);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (361, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_3.png', '3. 애호박은 반달로 썰어 식초에 살짝\n절여서 볶고, 양파는 채를 썰어 물에\n잠시 담가 놓는다.', 75);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (362, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_4.png', '4. 아몬드는 우유를 넣어 갈아 준비한다.', 75);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (363, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_5.png', '5. 머스터드에 올리고당을 넣어 잘 섞어\n준다.', 75);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (364, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00511_6.png', '6. 또띠아를 팬에 기름 없이 굽고, 그 안에\n닭고기 살과 느타리버섯, 오이, 사과,\n파프리카와 호박을 넣어 속이 보이도록\n말아놓고, ④번과 ⑤번은 섞어서 소스를\n만들어 완성한다.', 75);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (365, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_1.png', '1. 닭고기살은 소금과 후춧가루, 생강즙과\n정종을 뿌린 후 우유를 살짝 발라 숙성을\n시킨다.', 76);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (366, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_2.png', '2. 오이는 길게 껍질째 썰어 놓는다.', 76);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (367, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_3.png', '3. 토마토는 반으로 잘라 편으로 썰어\n준비한다.', 76);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (368, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_4.png', '4. 두부는 소창으로 물기를 꼭 짜 놓는다.', 76);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (369, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_5.png', '5. 물기를 제거 시킨 두부에 흑임자를\n넣고 골고루 섞은 뒤 냄비에 넣고\n우유와 함께 은근히 끓여 속을 만든다.', 76);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (370, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00512_6.png', '6. 팬에 토마토를 먼저 구워 접시에 담고,\n숙성된 닭고기를 구워 올린 뒤, 그 위에\n오이로 원형기둥을 만들고 구운\n닭고기를 다시 올려 완성한다.', 76);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (371, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_1.png', '1. 통오징어는 다리를 떼어내고 내장을\n제거시켜 통째로 깨끗이 씻어 준비\n한다.', 77);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (372, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_2.png', '2. 양파와 당근, 부추, 생강은 입자있게\n썰고, 견과류는 다져 준비한다.', 77);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (373, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_3.png', '3. 두부는 물기를 짠 후 수저로 으깨어\n놓는다.', 77);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (374, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_4.png', '4. 잘게 썰은 오징어 다리와 썰어 놓은\n채소, 견과류(②), 곱게 다진 대파와\n마늘, 그리고 소금과 녹말가루를 두부\n(③)와 함께 골고루 섞어 순대속을\n만든다.', 77);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (375, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_5.png', '5. 손질된 오징어 속 안에 준비한 속을\n채워 넣는다.', 77);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (376, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00513_6.png', '6. 오징어순대에 레몬즙과 정종, 생강즙을\n섞어 바른 뒤 찜통에 김이 오르면\n오징어순대를 넣어 약 10분 정도 쪄서\n완성한다.', 77);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (377, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_1.png', '1. 모시조개는 물에 소금을 넣고, 담그어\n해감을 한다.', 78);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (378, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_2.png', '2. 연어는 사각지게 썰어 준비한다.', 78);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (379, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_3.png', '3. 당근, 양파, 감자는 사각크기로 썰어\n놓고, 청피망은 채를 썰어 당근과\n비슷한 길이로 잘라둔다.', 78);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (380, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_4.png', '4. 냄비에 버터와 밀가루를 넣고 볶다가\n채소를 넣어 같이 볶는다.', 78);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (381, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_5.png', '5. 볶아지는 냄비에 모시조개와 육수를\n넣고 끓인다.', 78);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (382, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00514_6.png', '6. 거품을 걷어내고, 월계수 잎과 통후추,\n통마늘을 넣고 끓이다가 연어와\n생크림을 넣어 한 소큼 끓으면 완성한다.', 78);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (383, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_1.png', '1. 시금치와 브로콜리는 끓는 물에 소금을\n넣고 살짝 데친다.', 79);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (384, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_2.png', '2. 데친 시금치, 브로콜리와 바질을\n믹서에 갈아 준다.', 79);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (385, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_3.png', '3. 새우는 껍질을 벗겨 손질한다', 79);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (386, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_4.png', '4. 끓는 물에 스파게티를 10분 정도 삶아\n건진다.', 79);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (387, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_5.png', '5. 삶아진 스파게티는 팬에 올리브오일을\n넣고 볶는다.', 79);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (388, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00663_6.png', '6. 갈아 놓은 시금치소스에 생크림을 넣고\n한소끔 끓이면서 볶은 파스타를 넣은\n뒤 새우를 올린다', 79);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (389, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_1.png', '1. 광어는 비늘을 제거하고 포를 뜬다.', 80);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (390, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_2.png', '2. 손질된 광어에 레몬과 청주를 뿌려\n숙성 시킨다.', 80);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (391, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_3.png', '3. 저염간장에 레몬즙, 와사비,\n발사믹소스를 넣고 소스를 만든다.', 80);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (392, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_4.png', '4. 당근, 애호박은 쿠퍼로 동글동글하게\n깎는다.', 80);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (393, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_5.png', '5. 당근, 애호박, 콜리플라워는 김이 오른\n찜통에 5분 정도 쪄 낸다.', 80);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (394, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00664_6.png', '6. 가지와 파프리카는 팬에 굽고,\n숙성시킨 광어도 팬에 구워 접시에\n담고 쪄 낸 야채를 담은 뒤 소스를\n올린다.', 80);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (395, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_1.png', '1. 연근은 껍질을 벗긴다.', 81);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (396, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_2.png', '2. 껍질벗긴 연근은 얇게 썬다.', 81);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (397, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_3.png', '3. 물 200g에 식초 10g을 섞는다', 81);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (398, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_4.png', '4. 식촛물에 썬 연근을 담근다.', 81);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (399, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_5.png', '5. 연근을 체에 건져 물기를 뺀다.', 81);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (400, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00665_6.png', '6. 연근을 바삭하게 튀긴 뒤 천일염을\n살짝 뿌리고, 계피가루를 묻힌다.', 81);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (401, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_1.png', '1. 쌀은 깨끗이 씻어 30분 정도 불려 밥을\n짓는다.', 82);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (402, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_2.png', '2. 달걀을 풀어 소금을 넣고 지단을\n만들어 채썬다.', 82);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (403, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_3.png', '3. 도라지, 애호박, 양파, 홍고추는 채를\n썰어 소금에 살짝 절여 볶고, 청포묵은\n채를 썰어 참기름에 버무린다.', 82);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (404, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_4.png', '4. 돼지 안심은 소금, 후춧가루로 양념 한\n뒤 팬에 익힌다.', 82);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (405, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_5.png', '5. 찬물에 식초, 설탕, 소금을 넣고 채 썬\n오이를 넣어 냉국을 만든다.', 82);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (406, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00666_6.png', '6. 고추장에 고춧가루를 섞어 쌈밥장을\n만들고, 라이스페이퍼를 물에 담궈\n건져 밥과 준비한 야채, 달걀지단,\n쌈무, 쌈밥장을 올려 돌돌 말고 데친\n미나리로 묶는다.', 82);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (407, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_1.png', '1. 닭은 살을 발라 소금, 후춧가루로\n밑간을 한다.', 83);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (408, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_2.png', '2. 오곡을 불려 밥을 짓는다.', 83);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (409, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_3.png', '3. 오이, 아스파라거스, 당근, 양파는\n길이대로 채썬다.', 83);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (410, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_4.png', '4. 닭살에 지어 놓은 오곡밥을 넣는다', 83);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (411, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_5.png', '5. 오곡밥을 넣은 닭살에 카레가루를\n골고루 묻힌다.', 83);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (412, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00667_6.png', '6. 팬에 기름을 두르고 5번을 올려 은근히\n익혀 접시에 담고, 썰어 놓은 야채를\n닭살위에 담은 뒤 디종머스터드를\n올린다.', 83);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (413, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_1.png', '1. 광어는 비늘을 벗기고 포를 떠 소금,\n후춧가루를 뿌린다.', 84);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (414, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_2.png', '2. 콜리플라워는 팬에 볶는다.', 84);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (415, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_3.png', '3. 감자와 양파를 썰어 콜리플라워와 함께\n볶는다.', 84);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (416, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_4.png', '4. ?번에 우유를 넣는다.', 84);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (417, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_5.png', '5. ?를 믹서로 곱게 간 뒤, 끓인다.', 84);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (418, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00668_6.png', '6. 가지, 당근. 단호박은 둥근 모양으로\n만들어 팬에 시금치와 함께 익히고,\n광어도 팬에 구워 접시에 담아\n컬리플라워 소스를 올린다.', 84);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (419, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_1.png', '1. 다시마와 건새우를 넣고 은근히 끓여\n차갑게 식힌 뒤 육수를 만든다.', 85);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (420, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_2.png', '2. 차갑게 식힌 육수는 체에 걸려내고\n오미자를 넣고 소금으로 간을 한다.', 85);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (421, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_3.png', '3. 콜라비, 피망, 파프리카는 납작하게\n썬다.', 85);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (422, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_4.png', '4. 육수는 체에 거른다', 85);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (423, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_5.png', '5. 오미자를 넣고 소금으로 간을 한다.', 85);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (424, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00669_6.png', '6. 통에 썰어놓은 야채와 무화과를 담고\n?를 넣는다.', 85);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (425, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_1.png', '1. 닭가슴살은 포를 떠 소금과 후춧가루로\n밑간을 한다.', 86);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (426, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_2.png', '2. 새우는 껍질을 벗겨 손질한다.', 86);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (427, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_3.png', '3. 베이컨은 끓는 물에 데친다.', 86);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (428, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_4.png', '4. 마늘과 바질은 다진 뒤 버터에 섞는다.', 86);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (429, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_5.png', '5. 닭가슴살에 베이컨과 다진 버터를\n올린다.', 86);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (430, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00670_6.png', '6. 냄비에 마늘과 바질을 섞은 버터,\n치즈를 넣고 끓이다가 ?를 놓고\n졸인다.', 86);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (431, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_1.png', '1. 표고버섯은 따뜻한 물에 불린다.', 87);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (432, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_2.png', '2. 감자는 강판에 간다.', 87);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (433, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_3.png', '3. 고수는 잘게 다진다.', 87);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (434, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_4.png', '4. 당근은 곱게 다지고, 양파와 홍고추도\n다진다.', 87);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (435, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_5.png', '5. 감자에 다진 당근과 양파, 홍고추를\n섞는다.', 87);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (436, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00671_6.png', '6. 불린 표고버섯은 기둥을 자르고 바닥에\n녹말가루를 묻혀 ?번을 넣고 김이\n오른 찜통에 약 10분 정도 찐다.', 87);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (437, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_1.png', '1. 새우는 껍질을 벗겨 다진다.', 88);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (438, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_2.png', '2. 오징어는 소금으로 껍질을 벗겨\n다진다.', 88);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (439, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_3.png', '3. 애호박, 깻잎, 대추는 곱게 채썬다.', 88);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (440, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_4.png', '4. 채썬 애호박과 깻잎은 소금에 살짝\n절인 뒤 물기를 짠다.', 88);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (441, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_5.png', '5. 밀가루에 찹쌀가루, 애호박, 깻잎,\n대추채와 다진 새우, 오징어를 넣고\n팬에 기름을 둘러 전병을 만든 뒤 돌돌\n말아 썰어 접시에 담는다.', 88);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (442, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00672_6.png', '6. 저염간장과 식초, 설탕을 넣고 소스를\n만든다.', 88);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (443, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-1.jpg', '1. 닭다리는 기름기를 제거하고 냄비에 물 3컵 반을 부어 통마늘과 삶아 육수를 만든다.', 89);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (444, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-2.jpg', '2. 삶은 마늘을 체에 걸러 으깬 뒤 다시 육수에 넣는다.', 89);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (445, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-3.jpg', '3. 닭 육수 2컵 반에 찬밥을 넣고 밥알이 무르도록 끓인다.', 89);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (446, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-4.jpg', '4. 삶은 닭다리 살을 발라 잘게 찢어 넣는다.', 89);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (447, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-5.jpg', '5. 감자, 양파, 당근, 부추는 잘게 다진다.', 89);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (448, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/673-6.jpg', '6. 4에 5를 넣고 끓인 뒤 소금, 후춧가루로 간 한다.', 89);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (449, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-1.jpg', '1. 물 1컵 반에 국멸치와 건다시마를 넣어 멸치다시마국물을 만든다.', 90);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (450, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-2.jpg', '2. 국물을 우린 다시마는 채썬다.', 90);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (451, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-3.jpg', '3. 느타리버섯은 끓는 물에 데친 후 찢는다.', 90);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (452, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-4.jpg', '4. 감자와 두부는 3~4cm 가량으로 채를 썰고 홍고추와 대파는 어슷썬다.', 90);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (453, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-5.jpg', '5. 멸치다시마국물 1컵에 감자를 넣고 끓인 뒤 느타리버섯을 넣어 한소끔 끓이고 두부, 파, 다진 마늘, 홍고추를 넣고 소금으로 간 한다.', 90);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (454, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/674-6.jpg', '6. 채 썬 다시마를 고명으로 얹는다', 90);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (455, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-1.jpg', '1. 양념장 재료를 모두 섞어 냉장고에서 하루 숙성시킨다.', 91);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (456, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-2.jpg', '2. 민어는 비늘을 긁고 지느러미, 내장을 제거하여 흐르는 물에 씻어 5cm 길이로 토막 낸다.', 91);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (457, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-3.jpg', '3. 소고기는 채 썰어 다진 마늘, 후추로 밑간한다.', 91);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (458, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-4.jpg', '4. 무는 나박 썰기, 애호박은 반달 썰기, 애느타리는 한입 크기로 찢고 고추와 대파는 어슷 썬다.', 91);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (459, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-5.jpg', '5. 냄비에 참기름을 두르고 소고기를 볶다가 물 1컵 반과 무를 넣고 양념장을 풀어 끓인다.', 91);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (460, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/677-6.jpg', '6. 무가 반 정도 익으면 민어와 채소를 넣고 한소끔 끓인 뒤 어슷 썬 대파와 생강즙을 넣어 살짝 끓이고 소금 간하고 쑥갓을 넣는다.', 91);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (461, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_1.png', '1. 냄비에 육수 재료와 물(400g)을\n넣고 푹 끓인다.', 92);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (462, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_2.png', '2. 양송이버섯을 잘게 다진 뒤 버터에\n살짝 볶아 건져 한김 식힌 뒤\n닭다리살과 닭가슴살, 아몬드를 다져\n볶은 양송이버섯과 섞어 동그랗게\n빚어 밀가루옷을 입힌다.', 92);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (463, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_3.png', '3. 식용유(15g)를 두른 팬에서 겉을\n노릇하게 굽는다.', 92);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (464, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_4.png', '4. 육수(150g), 우유, 들깻가루를 끓여\n소스를 만든 뒤 아몬드 치킨볼과\n브로콜리를 넣고 졸인다.', 92);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (465, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_5.png', '5. 마늘은 편 썰어 낮은 온도의\n식용유에서 바삭하게 튀긴다.', 92);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (466, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00297_6.png', '6. 접시에 아몬드치킨볼을 담고\n튀긴 마늘을 얹어 마무리한다.', 92);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (467, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_1.png', '1. 현미와 쌀을 씻어 30분간 불린 뒤\n물기를 빼고, 다시마를\n우려낸 물(불린 쌀 동량)로 밥을 지어\n식힌 후 새우가루를 넣어 섞는다.', 93);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (468, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_2.png', '2. 양배추는 15~20분 정도 쪄서\n준비한다.', 93);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (469, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_3.png', '3. 얇게 저민 쇠고기를 팬에 올려\n익힌다.', 93);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (470, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_4.png', '4. 달래는 0.5㎝ 정도로 송송 썰어\n간장, 참기름에 버무린다.', 93);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (471, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_5.png', '5. 김발에 양배추, 고기, 밥 순서대로\n올려 돌돌 말아 양배추롤을 만든다.', 93);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (472, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00298_6.png', '6. 롤을 한입 크기로 썰어 담고\n달래 무침을 얹은 뒤 송송 썬\n붉은 고추를 올려 마무리한다.', 93);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (473, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_1.png', '1. 피망, 파프리카는 5㎝ 길이로 채\n썰고, 시금치는 갈아둔다.', 94);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (474, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_2.png', '2. 베이컨 위에 동태포를 올리고\n그 위에 피망과 파프리카를 올려\n돌돌 만다.', 94);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (475, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_3.png', '3. 저염버터에 생크림을 넣어서 농도를\n맞춘 후 갈아놓은 시금치를 넣어\n시금치소스를 만든다.', 94);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (476, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_4.png', '4. 달군 팬에 흰살생선베이컨말이를\n올려 완전히 익힌다.', 94);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (477, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00299_5.png', '5. 접시에 담아 시금치소스를\n곁들인 뒤 새싹채소로 장식하여\n마무리한다.', 94);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (478, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_1.png', '1. 콩고기를 15분~20분 정도 물에\n불려 물기를 완전히 제거한 뒤\n저염간장양념으로 버무려 볶는다.', 95);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (479, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_2.png', '2. 아보카도, 바나나, 가지, 연근은\n깍둑 썰고, 방울토마토는 반을\n가른다.', 95);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (480, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_3.png', '3. 팬에 식용유(5g)를 둘러가며\n방울토마토, 가지, 연근을 각각\n볶아 건진다.', 95);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (481, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_4.png', '4. 만두피를 작은 체에 넣고\n바구니 모양으로 만들어 튀긴다.', 95);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (482, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00300_5.png', '5. 콩고기와 아보카도, 바나나,\n방울토마토, 가지, 연근을 튀긴\n만두피에 담은 뒤 허브오일드레싱을\n곁들여 마무리한다.', 95);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (483, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_1.png', '1. 수박껍질은 흰 부분만 사용해\n작게 깍둑 썰고, 토마토는 껍질을\n벗겨 속씨를 제거해 작게 깍둑썬다.', 96);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (484, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_2.png', '2. 닭가슴살은 수비드해 익힌 뒤\n한입 크기로 썬다.', 96);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (485, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_3.png', '3. 감자, 가지, 양파, 피망,\n아스파라거스는 깍둑 썬 뒤\n닭가슴살과 함께 볶는다.', 96);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (486, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_4.png', '4. 팬에 복분자를 넣고 살짝 졸인 뒤\n레몬즙을 섞어 복분자소스를 만든다.', 96);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (487, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_5.png', '5. 접시에 퀴노아를 두른 후\n닭가슴살과 감자, 가지, 양파, 피망,\n아스파라거스를 올리면서 중간\n중간에 토마토와 수박,\n리코타치즈를 넣는다.', 96);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (488, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00301_6.png', '6. 맨 위에 퀴노아를 살짝 올려주고\n양상추, 플레인 요구르트,\n어린잎채소를 얹어 마무리한다.', 96);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (489, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_1.png', '1. 바나나를 반으로 잘라 반은 편 썰고\n반은 으깨고, 아보카도는 껍질과\n씨를 제거 한 후 ⅓은 편 썰고\n⅔는 으깬다.', 97);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (490, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_2.png', '2. 토마토는 끓는 물에 데쳐 껍질을\n제거하고 얇게 썬다.', 97);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (491, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_3.png', '3. 베이컨 2장은 채 썰고,\n1장은 구운 뒤 비슷한 크기로\n채 썬다.', 97);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (492, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_4.png', '4. 베이컨을 구운 기름에 편 썬 바나나,\n아보카도, 토마토를 넣고 바질가루를\n뿌려 굽는다.', 97);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (493, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_5.png', '5. 팬에 채 썬 베이컨과 다진 마늘,\n다진 양파를 볶은 후 휘핑크림,\n저염치즈를 넣고 볶다가 으깬\n아보카도, 바나나, 우유를 넣어\n농도를 맞추고 소금으로 간해\n바나나크림을 만든다.', 97);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (494, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00302_6.png', '6. 접시에 구운 바나나, 아보카도,\n토마토를 깔고 삶은 스파게티면을\n담은 후 바나나크림을 뿌리고\n베이컨을 올려 장식해 마무리한다.', 97);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (495, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_1.png', '1. 신선초, 참나물, 취나물, 적겨자는\n먹기 좋은 크기로 뜯고,\n깻잎은 한입 크기로 썬다.', 98);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (496, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_2.png', '2. 양파(8g)는 채 썰고, 파스타소스에\n들어갈 양파(15g)는 굵게 다지고,\n애호박도 양파와 비슷한 크기로\n굵게 다진다.', 98);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (497, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_3.png', '3. 오이는 돌려 깎은 뒤 채 썰고,\n고추도 얇게 채 썬다.', 98);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (498, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_4.png', '4. 끓는 물에 쌀파스타면을 넣고 5분간\n삶아 익힌 뒤 찬물에 헹궈 물기를\n뺀다.', 98);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (499, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_5.png', '5. 팬에 물(60g)을 붓고 물이 끓으면\n된장, 청국장, 다진 양파, 애호박,\n으깬 두부, 다진 마늘을 넣고\n1분 30초 정도 졸여 파스타소스를\n만든다.', 98);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (500, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00303_6.png', '6. 쌀파스타면 위에 소스를 얹은 뒤\n채소를 올리고 들기름을 뿌려\n마무리한다.', 98);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (501, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_1.png', '1. 감자는 한입 크기로 썰고, 양파와\n당근, 대파는 길게 채 썰고,\n청양고추는 다지고, 팽이버섯은\n깨끗이 씻어 준비한다.', 99);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (502, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_2.png', '2. 달걀은 노른자와 흰자를 분리해\n지단을 부쳐 채 썬다.', 99);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (503, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_3.png', '3. 당면은 찬물에 2시간 정도 불린다.', 99);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (504, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_4.png', '4. 물(300g)에 멸치를 넣어 육수를\n만든 후, 황태포를 넣어 다시\n한 번 진하게 육수를 우려낸다.', 99);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (505, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_5.png', '5. 육수에 감자, 당근, 양파, 당면을\n순서대로 넣고 끓인 후 불을 끄고\n팽이버섯을 얹어 잔열로 익힌다.', 99);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (506, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00304_6.png', '6. 다진 마늘, 청양고추, 저염간장, 소금,\n후춧가루를 넣어 간을 한 뒤 지단,\n쑥갓, 대파를 얹어 마무리한다.', 99);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (507, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_1.png', '1. 마늘 5g은 다지고, 나머지 마늘(15g)은\n편 썰어 노릇하게 튀긴다.', 100);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (508, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_2.png', '2. 깻잎은 채 썰고, 쪽파는 송송 썰고,\n파프리카는 주사위 모양으로 썰고,\n양파와 대파는 채 썰고,\n방울토마토는 반을 갈라 준비한다.', 100);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (509, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_3.png', '3. 닭가슴살은 어슷하게 납작 썰어\n이태리시즈닝에 재운 뒤 팬에 구워\n준비한다.', 100);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (510, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_4.png', '4. 다진 마늘, 양파, 대파를 팬에 넣고\n향을 내다 파프리카와 방울토마토,\n양념을 넣고 볶은 뒤 한 김 식혀\n믹서에 곱게 갈아 소스를 만든다.', 100);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (511, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_5.png', '5. 팬에 소스와 구운 닭가슴살을 넣어\n볶다가 삶은 라면을 넣고 재빠르게\n볶는다.', 100);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (512, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00305_6.png', '6. 튀긴 마늘과 깻잎, 쪽파를 올려\n마무리한다.', 100);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (513, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_1.png', '1. 양파와 청경채, 파프라카는 채 썰고,\n쪽파는 송송 썬다.', 101);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (514, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_2.png', '2. 라면은 삶아서 체에 밭쳐 물기를\n제거한다.', 101);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (515, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_3.png', '3. 팬에 식용유(5g)를 두르고 다진\n마늘을 볶아 향을 낸 뒤 돼지고기를\n볶는다.', 101);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (516, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_4.png', '4. 채 썬 양파와 청경채, 파프리카를\n함께 넣고 볶다가 고추기름, 설탕,\n두반장, 라면스프로 간을 해\n볶는다.', 101);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (517, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00306_5.png', '5. 면을 함께 볶은 후 송송 썬 쪽파를\n뿌려 마무리한다.', 101);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (518, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_1.png', '1. 새우, 조개, 홍합은 손질한다.', 102);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (519, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_2.png', '2. 버섯은 먹기 좋은 크기로 썰고,\n양파는 채 썰고, 토마토는 씨를\n제거해 다진다.', 102);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (520, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_3.png', '3. 라면은 끓는 물에 데친 뒤 체에 밭쳐\n물기를 제거한다.', 102);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (521, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_4.png', '4. 냄비에 식용유(10g)를 두른 뒤\n새우와 조개, 홍합을 넣어 살짝 볶아\n건진다.', 102);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (522, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_5.png', '5. 같은 냄비에 토마토, 우유, 생크림을\n넣은 뒤 월계수잎과 정향을 넣고\n끓인다.', 102);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (523, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00307_6.png', '6. 끓으면 데친 라면, 새송이버섯, 홍합,\n새우, 조개, 양파를 넣고 끓여 다\n익으면 파르메산 치즈가루를\n뿌린 후 그 위에 파슬리가루를 뿌려\n마무리한다.', 102);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (524, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_1.png', '1. 라면을 살짝 익힌다.', 103);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (525, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_2.png', '2. 배와 파인애플을 한입 크기로\n썰고, 파프리카는 깍둑 썬다.', 103);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (526, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_3.png', '3. 팬에 쇠고기 양념장과 파인애플을\n넣어 끓이다가 채 썬 쇠고기를\n넣는다.', 103);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (527, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_4.png', '4. 레드와인을 넣고 알코올을 날린 뒤\n매실청을 넣고 조린다.', 103);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (528, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_5.png', '5. 라면과 파프리카, 배를 섞은 후\n라이스페이퍼에 올려 만다.', 103);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (529, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00308_6.png', '6. 달걀물을 묻힌 후 빵가루를 입혀\n튀긴 뒤 그릇에 어린잎채소와 함께\n담아 조린 쇠고기를 곁들여 마무리\n한다.', 103);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (530, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_1.png', '1. 돼지고기를 차가운 물로 헹구고\n냄비에 물(300g), 대파, 생강과 같이\n넣고 끓여 돼지고기가 익으면\n면포에 거른다.', 104);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (531, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_2.png', '2. 냄비에 물(300g)을 넣어 끓으면\n가다랑어포를 넣고 1분간 끓이고\n불을 꺼 가다랑어포가 가라앉으면\n면포에 거르고, 다시 물(300g)을\n넣어 면포에 걸러낸 가다랑어포를\n넣어 약한 불로 10분간 끓인 뒤\n가다랑어포가 가라앉으면 면포에\n거른다.', 104);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (532, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_3.png', '3. 돼지고기 육수와 가다랑어포 육수를\n섞은 뒤 200g을 따로 덜어\n조림양념을 넣고 끓이다가\n돼지고기를 넣고 조린다.', 104);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (533, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_4.png', '4. 라면은 삶아 찬물에 헹궈 건지고,\n쪽파는 송송 썬다.', 104);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (534, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00309_5.png', '5. 육수를 뜨겁게 끓인 뒤 그릇에\n라면사리와 함께 담고,\n돼지고기조림, 쪽파를 올리고 팬에\n남은 조림 소스를 끼얹어 마무리한다.', 104);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (535, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_1.png', '1. 끓는 물(600g)에 라면을 넣어\n데친다.', 105);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (536, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_2.png', '2. 면은 찬물에 헹궈 물기를 뺀다.', 105);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (537, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_3.png', '3. 양상추, 케일, 파프리카는 얇게 채\n썰고, 비타민은 먹기 좋은 크기로\n자른다.', 105);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (538, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00310_4.png', '4. 유자드레싱을 고루 섞은 뒤 라면,\n채소를 버무려 마무리한다.', 105);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (539, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_01.png', '1. 주꾸미를 몸과 다리를 분리 후 잘 씻어준다.', 106);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (540, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_02.png', '2. 주꾸미 다리, 미나리, 양파, 당근을 다져준다.', 106);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (541, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_03.png', '3. 다진 채소를 계란, 다진마늘, 생강청에 양념\n한다.', 106);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (542, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_04.png', '4. 양념한 3을 주꾸미 머리 속에 넣어주고\n이쑤시개로 꽂아준다.', 106);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (543, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_05.png', '5. 찜기에 찐다. 찔 때 레몬즙을 뿌리면 더욱\n탱탱하다.', 106);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (544, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00414_06.png', '6. 소스를 만든 뒤 주꾸미를 함께 완성한다.', 106);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (545, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_01.png', '1. 무는 모양 틀로 자른다.', 107);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (546, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_02.png', '2. 무와 연근을 손질해 놓은 후 소금에 살짝\n절여준다.', 107);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (547, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_03.png', '3. 사과와 배는 먹기 좋게 잘라 레몬즙에 뿌려\n갈변을 막아준다.', 107);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (548, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_04.png', '4. 물에 청양고추를 넣어 끓여서 식힌다.', 107);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (549, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_05.png', '5. 식힌 물에 국물 재료를 넣어 국물을 만든다.', 107);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (550, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00415_06.png', '6. 만들어진 국물에 절여진 무, 연근, 사과, 배를\n넣어주어 완성한다. 24시간 이후부터 시원하게\n먹는다.', 107);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (551, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_01.png', '1. 다진마늘을 버터에 충분히 볶아준다.', 108);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (552, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_02.png', '2. 화이트와인을 뿌려주고 소스재료로 소스를\n만들어준다.', 108);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (553, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_03.png', '3. 찜기에 살짝 져 준다. 이때 80% 정도까지만\n익혀준다.', 108);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (554, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_04.png', '4. 쪄준 조기는 와인과 후추로 밑간을 해준 후\n전분을 발라준다.', 108);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (555, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_05.png', '5. 전분를 바른 조기에 허브크러스트를 두툼하게\n올려준다.', 108);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (556, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00416_06.png', '6. 팬에 기름을 두르고 조기를 한쪽부터 구운 뒤,\n뒤집어서 다시 구워주고 소스를 뿌려 완성한다.', 108);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (557, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_01.png', '1. 무, 청고추, 미나리, 대파는 먹기 좋게 썰어준다.', 109);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (558, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_02.png', '2. 돼지머리는 냉수에 데친다. 냉수일 때부터\n돼지머리를 넣어야 물이 끓어오르면 불순물이\n제거된다.', 109);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (559, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_03.png', '3. 일본 된장을 풀어 체에 걸러준다.', 109);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (560, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_04.png', '4. 돼지머리를 살짝 졸여준다.', 109);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (561, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_05.png', '5. 냄비에 국물을 끓여주다가 채소를 넣어 끓인다.', 109);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (562, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00417_06.png', '6. 국물에 우동면을 데쳐낸 뒤 완성한다.', 109);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (563, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_01.png', '1. 양파, 2가지색 파프리카, 청고추는 작은 깍두기\n모양으로 잘라준다.', 110);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (564, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_02.png', '2. 파인애플도 먹기 좋게 썰어준 뒤 레몬소금을\n살짝 뿌려준다.', 110);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (565, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_03.png', '3. 계란은 풀어서 팬에 스크램블을 만들어준다.', 110);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (566, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_04.png', '4. 마늘을 다진 후 마늘기름으로 칵테일 새우를\n볶아준다.', 110);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (567, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_05.png', '5. 볶아진 칵테일새우에 잘라준 채소를 넣어\n다시 볶아준다.', 110);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (568, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00418_06.png', '6. 여기에 밥을 넣어서 볶아주고 파인애플과\n맛간장, 통깨를 넣어서 볶아준다.', 110);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (569, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_01.png', '1. 양파는 채 썰어 물에 담가주고, 오이는 돌려\n깎기 해서 물에 담가준다.', 111);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (570, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_02.png', '2. 연근은 껍질을 까고 얇게 썰어서 식초 2방울을\n넣은 물에 데쳐준다.', 111);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (571, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_03.png', '3. 밤도 아주 얇게 썰어서 물에 담가준다.', 111);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (572, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_04.png', '4. 소스 재료를 모두 넣고 믹서에 간다.', 111);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (573, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_05.png', '5. 물에 데쳐낸 연근은 키친타월에 올려서 물기를\n완전히 제거한다.', 111);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (574, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00419_06.png', '6. 모든 재료를 넣고 소스에 버무려준다.', 111);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (575, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_01.png', '1. 코다리는 어간장, 청주, 흰후추, 생강청에\n살짝 재워준다.', 112);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (576, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_02.png', '2. 함초는 손질해 잘라준다.', 112);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (577, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_03.png', '3. 무는 소금에 살짝 절여서 수분과 염분을\n제거해준다.', 112);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (578, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_04.png', '4. 무와 함초를 고루 섞어준다.', 112);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (579, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_05.png', '5. 미나리와 콩나물은 손질해준 후 찜기에 깔고\n코다리를 쪄준다.', 112);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (580, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00420_06.png', '6. 소스를 만든 뒤 채소를 깔고 코다리를 올리고\n미나리, 콩나물을 올려 완성한다.', 112);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (581, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_01.png', '1. 두부는 물기를 꼭 짠 뒤 으깨어 전분, 계란과\n섞어준다.', 113);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (582, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_02.png', '2. 우엉, 인삼, 감자는 아주 곱게 채 썰어서 냉수에\n담가준다.', 113);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (583, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_03.png', '3. 볶아진 모든 채소를 각각 볶아서 한데 섞어\n준다.', 113);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (584, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_04.png', '4. 땅콩잼으로 소스를 만들어준다.', 113);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (585, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_05.png', '5. 김발을 깔고 비닐을 놓아 준 후 두부를 펴준다.', 113);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (586, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00421_06.png', '6. 볶아진 채소를 두부에 넣어주고 찜기에 쪄서\n식힌 뒤 한입 크기로 잘라준다.', 113);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (587, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_01.png', '1. 사과는 8조각으로 잘라 씨를 제거한다.', 114);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (588, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_02.png', '2. 청고추, 홍고추는 어슷하게 잘라준다.', 114);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (589, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_03.png', '3. 양파도 먹기 좋은 크기로 잘라준다.', 114);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (590, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_04.png', '4. 사과는 식초 물에 살짝 절여준다.', 114);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (591, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_05.png', '5. 양념장을 만든다.', 114);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (592, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00422_06.png', '6. 양념장에 준비된 재료를 모두 섞어 완성한다.', 114);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (593, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_01.png', '1. 주꾸미, 전복, 칵테일새우는 각각 물에 데쳐서\n준비한다.', 115);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (594, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_02.png', '2. 이때 데치고 남은 물을 국물에 이용해주고\n여기에 청주를 넣어준다.', 115);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (595, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_03.png', '3. 양파, 청고추, 홍고추는 먹기좋게 썰어준다.', 115);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (596, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_04.png', '4. 계란을 황백으로 부쳐서 먹기좋게 썰어준다.', 115);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (597, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_05.png', '5. 만들어진 국물에 거피한 들깨가루와 국물재료를\n넣어 끓여서 준비한다.', 115);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (598, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00423_06.png', '6. 준비된 재료를 냄비에 넣고 국물을 부어 끓여\n완성한다.', 115);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (599, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_01.png', '1. 참외는 껍질을 벗기고 반으로 잘라 속을\n파 준 후 먹기 좋게 잘라준다.', 116);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (600, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_02.png', '2. 청고추, 홍고추는 송송 잘라준다.', 116);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (601, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_03.png', '3. 양파와 미나리는 참외크기로 자르고, 대파는\n송송 자른다.', 116);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (602, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_04.png', '4. 참외, 양파를 저염 간장에 살짝 절여준다.', 116);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (603, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_05.png', '5. 절여진 채소에 고춧가루로 먼저 색을 입힌다.', 116);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (604, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00424_06.png', '6. 나머지 재료를 넣고 김치를 만든다.', 116);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (605, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_01.png', '1. 연어는 얇게 모양을 살려 슬라이스 한 후\n정종에 소금을 섞어 분무기에 넣어 냉장고에\n넣어둔다.', 117);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (606, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_02.png', '2. 건조된 방울토마토는 올리브오일과 후추로\n양념하여 사용한다.', 117);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (607, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_03.png', '3. 양상추는 먹기 좋은 크기로 뜯어 어린잎과\n함께 찬물에 담가 물기를 제거한 후 사용한다.', 117);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (608, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_04.png', '4. 유자청과 감식초는 함께 섞어 믹서에 갈아\n드레싱소스를 완성한다.', 117);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (609, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_05.png', '5. 양파는 모양을 살려 얇게 슬라이스 하여\n찬물에 담가 물기를 제거한 뒤 사용한다.', 117);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (610, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00425_06.png', '6. 샐러드 접시에 준비된 모든 채소를 담고,\n연어는 장미 모양으로 접어서 보기좋게 담아\n드레싱 소스를 곁들여 완성한다.', 117);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (611, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_01.png', '1. 콜라비는 껍질을 벗긴 후 저염간장에 30분\n정도 절여준다.', 118);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (612, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_02.png', '2. 우렁은 뜨거운 물에 데친 후 행군 뒤 물기를\n빼준다.', 118);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (613, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_03.png', '3. 미나리는 먹기 좋은 크기로 잘라준다.', 118);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (614, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_04.png', '4. 다진대파, 청홍고추, 양파는 송송 잘라준다.', 118);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (615, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00426_05.png', '5. 양념장에 콜라비부터 버무린 뒤 모든 재료를\n섞어서 완성한다.', 118);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (616, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_01.png', '1. 코다리는 먹기 좋게 잘라서 물에 헹궈 물기를\n빼준다.', 119);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (617, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_02.png', '2. 감자와 무, 백일송이버섯, 미나리는 먹기 좋게\n썰어준다.', 119);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (618, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_03.png', '3. 양파, 청양고추도 먹기 좋게 썰어준다.', 119);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (619, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_04.png', '4. 냄비에 참기름을 넣어 코다리를 살짝 볶아준다.', 119);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (620, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_05.png', '5. 코다리를 볶은 냄비에 무, 감자, 국물 재료를\n넣고 충분히 끓인다.', 119);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (621, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00427_06.png', '6. 끓고 있는 국에 양파, 청양고추, 백일송이\n버섯을 올려 완성한다.', 119);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (622, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_1.png', '1. 연두부는 동글게 썰어 준비한다.', 120);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (623, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_2.png', '2. 호박은 갈라 씨를 제거 하고 껍질을\n벗겨 쪄서 작은 깍둑썰기로 썰어 준비\n한다.', 120);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (624, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_3.png', '3. 바나나와 파프리카 토마토는 호박과\n같은 크기의 작은 깍두기로 썰어 놓는다.', 120);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (625, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_4.png', '4. 닭고기살은 통후추를 넣고 약 20분 정도\n삶아 건져 작은 깍두기로 썰어 준비한다.', 120);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (626, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_5.png', '5. 마와 잣, 흑임자를 갈아 놓은 뒤 생크림,\n요거트, 식초, 올리브오일, 설탕을\n섞어 샐러드 소스를 만들어 놓는다.', 120);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (627, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00530_6.png', '6. 썰어 놓은 연두부 위에 호박 , 닭고기,\n토마토, 파프리카를 각각 올린 뒤 어린\n잎을 올리고 소스를 뿌려 완성한다.', 120);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (628, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_1.png', '1. 애호박은 반달 모양으로 썰어 준다.', 121);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (629, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_2.png', '2. 썰어 놓은 호박에 소금을 넣고 살짝\n절여 물기를 제거하고 준비한다.', 121);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (630, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_3.png', '3. 양파는 채를 썰고, 대파는 1/4로 자르고,\n홍고추는 어슷썰어 자연스럽게 씨를\n제거해 놓는다.', 121);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (631, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_4.png', '4. 팬에 들기름을 넣고, 마늘을 먼저\n볶는다.', 121);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (632, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_5.png', '5. 볶아진 팬에 건새우를 넣어 볶아준다.', 121);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (633, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00531_6.png', '6. 건새우가 볶아지면, 썰어 놓은 호박을\n넣어 볶다가 양파와 대파를 넣어\n볶으면서 들깨 가루와 넣어 살짝\n볶으면서 마지막에 썰어 놓은 홍고추를\n넣어 한 번만 더 볶아 완성한다.', 121);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (634, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_1.png', '1. 고구마는 찜통에 약 20분 정도 쪄서\n껍질을 벗겨 준비한다.', 122);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (635, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_2.png', '2. 뜨거운 고구마가 한김 빠지면, 곱게\n으깨 놓는다.', 122);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (636, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_3.png', '3. 으깨진 고구마에 바나나를 썰어\n요거트와 함께 넣는다.', 122);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (637, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_4.png', '4. 3번에 다시 연유를 넣어 골고루 섞는다.', 122);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (638, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_5.png', '5. 잣은 종이를 깔고 곱게 다져 준비한다.', 122);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (639, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00532_6.png', '6. 어린잎은 물에 담그어, 건져 물기를\n제거해 놓고, 으깬 고구마에 다진 잣을\n넣어 샐러드를 완성한다.', 122);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (640, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_1.png', '1. 새우는 껍질을 제거하고 소금과\n후춧가루를 넣어 밑간을 해놓는다.', 123);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (641, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_2.png', '2. 어린잎은 찬물에 담그어 놓는다.', 123);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (642, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_3.png', '3. 튀김가루에 달걀을 넣어 잘 섞어\n튀김반죽을 만들어 놓는다.', 123);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (643, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_4.png', '4. 밑간을 해 놓은 새우를 튀김반죽에\n넣어 옷을 입힌다.', 123);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (644, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_5.png', '5. 누룽지는 170℃의 온도 튀김 기름에\n튀겨내고, 튀김옷 입힌 새우도 튀겨낸다.', 123);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (645, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00533_6.png', '6. 냄비에 오렌지주스와 설탕, 식초를\n넣어 은근히 졸여 소스를 만들고,\n어린잎은 찬물에 건져 물기를 제거하고\n접시에 깔고 튀긴 누룽지와 튀긴 새우를\n올려 완성한다.', 123);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (646, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_1.png', '1. 백김치는 살짝 씻어 물기를 제거하고\n잘게 썰어 준비한다.', 124);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (647, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_2.png', '2. 양파와 마늘은 다지고 파는 채를 썰어\n놓는다.', 124);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (648, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_3.png', '3. 어린잎은 찬 물에 담그어 놓았다가,\n다시 소창에 건져 물기를 빼 놓는다.', 124);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (649, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_4.png', '4. 냄비에 물을 넣고, 양파와 파를 넣어\n끓이다가 준비한 쭈구미를 넣고\n부드럽게 데쳐 썰어 놓는다.', 124);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (650, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_5.png', '5. 식초와 간장, 설탕, 꿀, 레몬, 다진양파,\n다진마늘을 넣고 잘 섞어서 샐러드\n드레싱을 만든다.', 124);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (651, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00534_6.png', '6. 준비한 백김치(①)와 주꾸미(②)를\n섞은 후 양상추를 접시에 깔고, 그 위에\n섞어놓은 백김치와 주꾸미를 올리고,\n홍고추와 어린잎을 위에 장식으로 올린\n후 샐러드 소스를 뿌려 완성한다.', 124);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (652, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_1.png', '1. 닭고기살에 로즈마리를 뿌려 약 3시간\n이상 숙성을 시킨다.', 125);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (653, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_2.png', '2. 냄비에 물을 1/3쯤 넣고, 양파채를 썰어\n넣어 끓으면, 숙성된 닭고기살을 넣고\n약 20분 정도 삶아 건진다.', 125);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (654, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_3.png', '3. 삶아 건진 닭고기살을 한 김 빼고, 길게\n손으로 찢어 소금과 후춧가루에 살짝\n무치고, 오이와 파프리카는 씨를 제거\n하고 채를 썰고, 당근도 오이채의 길이에\n맞게 채를 썰어 준비해 놓는다.', 125);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (655, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_4.png', '4. 마늘을 다지고, 고춧가루와 설탕, 식초,\n레몬즙을 넣어 소스를 만든다.', 125);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (656, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_5.png', '5. 뜨거운 물에 라이스페이퍼를 살짝 넣어\n바로 건진다.', 125);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (657, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00535_6.png', '6. 건져 놓은 라이스페이퍼에 준비한\n닭고기살과 파프리카, 오이, 당근 넣고\n돌돌 말아, 녹말가루를 살짝 입혀, 팬에\n굴리듯 익혀 완성하고, 준비해 놓은\n소스와 함께 제공한다.', 125);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (658, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_1.png', '1. 단호박은 1/8 등분으로 갈라 속을\n파내고 찜통에서 약 15~20분 정도\n충분히 쪄낸다.', 126);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (659, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_2.png', '2. 소고기는 채를 썰어 마늘, 간장, 설탕에\n양념을 하고 팬에 볶아서 식힌다.', 126);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (660, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_3.png', '3. 파프리카와 오이, 적양배추는 채를\n썰고, 무순은 물에 잠시 담그어 놓고,\n사과는 채를 썰어 설탕물에 잠시 담그어\n놓고, 팽이버섯은 밑둥만 잘라 한 줌\n집기 좋은 크기로 준비해 놓는다.', 126);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (661, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_4.png', '4. 다진마늘과 다진 양파 ,올리브오일,\n설탕, 식초, 소금을 넣고, 골고루 섞어\n소스를 만들어 놓는다.', 126);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (662, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_5.png', '5. 쪄 낸 단호박은 껍질을 벗기고 채에\n곱게 내려 밀가루와 물, 소금을 조금\n섞어 단호박 반죽을 만들어 놓는다.', 126);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (663, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00537_6.png', '6. 팬에 올리브 오일을 바르고, 만들어\n놓은 단호박 반죽을 한 국자씩 떠 넣어,\n밀전병을 만들고, 밀전병이 익으면,\n한 김 식혀, 그 안에 소고기 볶음과\n채소를 넣어 돌돌 말아 소스와 함께\n완성한다.', 126);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (664, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_1.png', '1. 생선살에 생강즙과 정종을 넣고 약 30분\n정도 숙성을 시켜 비릿한 맛 제거 및 생선\n살의 탱탱함을 살려준다.', 127);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (665, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_2.png', '2. 콩가루와 밀가루, 카레가루, 녹말가루,\n달걀을 넣고 골고루 섞어 튀김 반죽을\n만들어 놓는다.', 127);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (666, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_3.png', '3. 깻잎은 반으로 접어 채를 썰어 물에\n잠시 담그어 놓는다.', 127);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (667, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_4.png', '4. 깻잎(③)은 물기를 제거하고\n튀김반죽(②)을 골고루 입힌다.', 127);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (668, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_5.png', '5. 숙성시킨 생선살(①)에 녹말가루를\n살짝 입히고 튀김 반죽(②)을 골고루\n입힌 뒤, 튀김반죽을 입힌 깻잎(④)을\n얹어 튀김 기름 170℃ 온도에 넣어\n약 2분 정도 튀겨 기름을 자연스럽게\n빼 놓는다.', 127);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (669, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00538_6.png', '6. 마요네즈와 요구르트, 설탕, 식초를\n넣어 골고루 섞어 튀김소스를 만들고,\n튀김을 접시에 담아 양상추와 함께 얹어\n완성한다.', 127);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (670, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_1.png', '1. 썰어 놓은 삼겹살에 생갑즙과 정종을\n넣고 약 2시간 정도 숙성을 시켜 준비\n한다.', 128);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (671, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_2.png', '2. 가지는 씨가 적은 것으로 골라 반으로\n갈라 어슷썰어 놓는다.', 128);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (672, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_3.png', '3. 양파와 깻잎은 채를 썰고, 당근은\n직사각형으로 썰고, 대파는 송송 썰고,\n느타리는 손으로 찢어 준비한다.', 128);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (673, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_4.png', '4. 고춧가루과 다진마늘, 간장, 올리고당,\n후춧가루, 설탕을 넣고 골고루 섞어\n양념장을 만든다.', 128);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (674, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_5.png', '5. 숙성시켜 놓은 삼겹살에 만들어 놓은\n양념장을 넣고 골고루 무쳐 놓는다.', 128);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (675, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00539_6.png', '6. 팬에 기름을 살짝 둘러 양념한 고기(⑤)\n를 먼저 볶은 후 고기가 중간쯤 익으면,\n가지(②)와 썰어 놓은 채소(③)를 넣어\n볶다가 마지막에 참기름과 잣을 넣고\n한 번 더 볶은 후 통깨를 올려 완성한다.', 128);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (676, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_1.png', '1. 끓는 물에 식초 한 방울을 떨어 뜨려\n주꾸미를 데쳐 한김 식혀 먹기 좋게\n썰어놓는다.', 129);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (677, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_2.png', '2. 끓는 물에 식초 한 방울을 떨어 뜨려\n주꾸미를 데쳐 한김 식혀먹기 좋게\n썰어놓는다.', 129);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (678, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_3.png', '3. 양상추는 손으로 뜯어, 찬물에 담그어\n놓는다.', 129);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (679, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_4.png', '4. 미나리는 약 4cm 길이로 썰고, 홍고추는\n어슷 썰어 씨를 살짝 제거하고 피망은\n씨를 제거하고 미나리와 비슷한 길이로\n썰어놓는다.', 129);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (680, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_5.png', '5. 강판에 곱게 간 배즙과 무즙에 와사비,\n설탕, 다진마늘, 식초, 소금을 넣어\n샐러드소스를 만든다.', 129);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (681, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00540_6.png', '6. 만들어 놓은 샐러드소스와 함께 썰어\n놓은 주꾸미(②)와 채소(③, ④)를 넣어\n먹기 직전에 버무려 완성한다.', 129);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (682, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_1.png', '1. 닭가슴살에 칼을 1/3 쯤 넣고 양쪽으로\n포를 뜨고, 정종을 살짝 발라놓는다.', 130);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (683, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_2.png', '2. 포뜬 닭가슴살 위에 카레가루를 솔솔\n뿌려 숙성을 시킨다.', 130);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (684, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_3.png', '3. 현미쌀은 물에 충분히 불려, 물기를\n빼 놓고, 양배추와 적 양배추는 곱게\n채를 썰어 각각 찬물에 담그어 싱싱\n해 지면 건져 물기를 빼놓는다.', 130);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (685, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_4.png', '4. 냄비에 토마토소스를 끓이다가\n생크림을 넣고 살짝 한 번 더\n끓여놓는다.', 130);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (686, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_5.png', '5. 튀김기름에 온도가 약 170℃ 정도가\n되면, 불려 놓은 현미쌀의 물기를\n제거한 뒤 튀겨 기름을 제거해 놓는다.', 130);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (687, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00541_6.png', '6. 숙성된 닭가슴살(①)에 튀긴 현미쌀\n(⑤)을 앞뒤로 골고루 바르고 다시\n170℃ 온도의 튀김 기름에 약 3~5분\n정도 튀겨내고, 양배추와 적채를 깔고\n그 위에 튀겨진 닭가슴살을 올리고,\n토마토소스를 뿌려 완성한다.', 130);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (688, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_1.png', '1. 삼겹살에 소금, 후춧가루, 생강즙,\n정종을 넣고 약 30분 정도 숙성을 시킨다.', 131);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (689, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_2.png', '2. 조와 쌀은 물에 약 30분 정도 불려 밥을\n짓고, 밥이 다 익으면, 뚜껑을 열어 주걱\n으로 골고루 섞어 한 김을 빼 놓는다.', 131);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (690, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_3.png', '3. 파는 길게 채를 썰어 물에 담그어 파채가\n둥글게 말리도록 한다.', 131);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (691, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_4.png', '4. 설탕과 식초, 레몬, 소금, 고춧가루,\n매실액을 골고루 섞어 소스를 만들어\n놓는다.', 131);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (692, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_5.png', '5. 파프리카는 속을 제거하고 양파,\n당근, 파프리카를 양파와 같은 길이로\n채를 썰고, 새송이와 표고버섯은 살짝\n데쳐 채를 썰고, 깻잎도 채를 썰어\n놓는다.', 131);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (693, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00542_6.png', '6. 파프리카는 속을 제거하고 양파, 당근,\n파프리카를 양파와 같은 길이로 채를\n썰고, 새송이와 표고버섯은 살짝 데쳐\n채를 썰고, 깻잎도 채를 썰어 놓는다.', 131);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (694, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_1.png', '1. 미니버섯을 깨끗이 씻어 소금을 살짝\n뿌려 놓는다.', 132);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (695, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_2.png', '2. 녹말가루에 물을 반쯤 넣고, 가만히\n가라앉혀 윗물은 따라 버리고, 딱딱한\n된녹말을 만들어 놓는다.', 132);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (696, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_3.png', '3. 브로컬리는 끓는 물에 데쳐 작게 썰고,\n오이는 반달로 썰고, 당근은 은행잎\n모양으로 썰어 준비하고, 파프리카는\n속씨를 제거하고, 삼각지게 썰어 준비\n한다.', 132);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (697, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_4.png', '4. 간장과 홍초, 설탕을 넣어 골고루 섞어\n탕수육소스를 만들어 놓는다.', 132);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (698, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_5.png', '5. 소금을 뿌려 놓은 미니 버섯에 만들어\n놓은 된 녹말과 달걀흰자를 넣어 골고\n루 섞는다.', 132);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (699, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00543_6.png', '6. 튀김 기름의 온도가 170~180℃가\n되면, 녹말 입힌 미니버섯을 바삭 하게\n튀겨 주고, 다시 팬에 만들어 놓은\n탕수육소스를 바글바글 끓이다가,\n썰어 놓은 채소를 넣고 살짝 익힌 후,\n물녹말을 한 숟가락 정도 넣어 끓인 후\n튀겨진 미니버섯 위에 올려 완성해 준다.', 132);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (700, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_1.png', '1. 연어는 소금, 후춧가루, 레몬으로\n마리네이드 한다.', 133);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (701, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_2.png', '2. 양파, 마늘은 입자있게 다진다.', 133);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (702, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_3.png', '3. 토마토는 씨를 제거하고 입자있게\n썰고, 파프리카, 피망은 채 썬 뒤\n볶는다.', 133);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (703, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_4.png', '4. 화이트크림, 생크림을 골고루 섞어\n크림소스를 만든다.', 133);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (704, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_5.png', '5. 팬에 버터를 넣고 양파, 마늘을 볶다가\n만들어 놓은 크림소스를 넣고 졸인다.', 133);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (705, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00571_6.png', '6. 접시에 볶은 야채를 담고, 200℃\n오븐에서 10분 정도 구운 연어를 담은\n뒤 크림소스를 올린다.', 133);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (706, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_1.png', '1. 쌀은 깨끗이 씻어 30분 이상 불려 밥을\n지어 식힌다.', 134);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (707, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_2.png', '2. 옥수수콘은 체에 걸러 물기를 뺀다.', 134);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (708, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_3.png', '3. 양파, 애호박, 당근, 양파 및\n새송이버섯은 옥수수콘 크기로 썰고\n마늘은 다진다.', 134);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (709, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_4.png', '4. 팬에 기름을 두르고 야채를 먼저\n볶는다.', 134);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (710, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_5.png', '5. 볶은 야채에 식혀놓은 밥을 넣고\n볶다가 저염간장, 후춧가루로 간을\n하고 옥수수콘을 넣고 살짝 볶는다.', 134);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (711, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00572_6.png', '6. 팬에 기름을 자작하게 넣고,\n라이스페이퍼를 둥글게 넣고 튀겨\n그릇에 담은 뒤 볶음밥을 담는다', 134);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (712, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_1.png', '1. 연어는 끝을 뾰족하게 살려\n슬라이스한다.', 135);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (713, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_2.png', '2. 쌀은 깨끗이 씻어 30분 이상 불려 밥을\n지어 식힌다.', 135);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (714, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_3.png', '3. 냄비에 식초, 설탕, 소금 약간과 물을\n동량으로 넣고 끓인 뒤 식혀 촛물을\n만들어 밥에 넣고 골고루 섞는다.', 135);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (715, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_4.png', '4. 당근과 표고버섯은 입자있게 다져 살짝\n볶아 식혀놓은 초밥에 넣고 잘\n섞어준다.', 135);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (716, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_5.png', '5. 와사비 가루에 30℃정도의 물을 넣고\n섞은 뒤 발효 시키고, 초밥 형태를\n만들어 그 위에 와사비를 바르고\n연어를 올린 후 접시에 담는다', 135);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (717, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00573_6.png', '6. 새싹과 파슬리는 찬물에 담았다 건져\n물기를 빼고, 연어 초밥 옆에 올린다.', 135);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (718, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_1.png', '1. 소고기는 넓게 펴서 소금, 후춧가루로\n밑간을 하고 굽는다.', 136);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (719, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_2.png', '2. 닭가슴살은 소금, 후춧가루로 밑간을\n하여 굽고 길이대로 썬다.', 136);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (720, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_3.png', '3. 미나리는 끓는 물에 넣고 데쳐 찬물에\n헹군다.', 136);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (721, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_4.png', '4. 홍고추는 씨를 제거 하고 길게 썰어\n준비한다.', 136);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (722, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_5.png', '5. 달걀은 황백으로 나눠 지단을 부쳐\n길게 썰어 놓는다', 136);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (723, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00575_6.png', '6. 밑간한 소고기와 닭살 에 찹쌀가루를\n뭍혀 홍고추와 달걀 황백지단을 넣어\n돌돌 말아 미나리로 띠를 둘러 팬에\n기름을 살짝 둘러 익힌다.', 136);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (724, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_1.png', '1. 쌀은 깨끗이 씻어 30분 정도 불린다', 137);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (725, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_2.png', '2. 전복은 수저로 떼어 내어 소금으로\n세척한다.', 137);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (726, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_3.png', '3. 준비된 전복에 버터를 두르고 굽는다', 137);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (727, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_4.png', '4. 당근. 양파 . 양송이는 입자 있게\n다진다.', 137);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (728, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_5.png', '5. 냄비에 올리브 오일을 넣고 불린 쌀을\n볶는다.', 137);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (729, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00576_6.png', '6. 쌀이 어느 정도 볶아지면 다져 놓은\n야채를 넣어 볶다가 우유를 넣어\n끓이고, 쌀이 퍼지면 구워 놓은 전복을\n넣어 다시 끓인다.', 137);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (730, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_1.png', '1. 참깨와 흑임자를 섞어 곱게 간다.', 138);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (731, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_2.png', '2. 갈아 놓은 참깨와 흑임자에 설탕과\n식초를 넣고 섞는다.', 138);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (732, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_3.png', '3. 2번에 우유와 마요네즈를 넣고 골고루\n섞어 참깨 흑임자 드레싱을 만든다.', 138);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (733, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_4.png', '4. 연두부는 동그랗게 자른다.', 138);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (734, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_5.png', '5. 파프리카는 채 썬다.', 138);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (735, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00577_6.png', '6. 접시에 드레싱, 연두부, 파프리카,\n무순, 날치알 순으로 올린다.', 138);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (736, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_1.png', '1. 냄비에 마늘과 대파를 넣고 끓이다가\n닭다리를 넣고 삶는다', 139);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (737, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_2.png', '2. 닭을 삶아 놓은 육수는 걸러 기름을\n제거 하고 차갑게 식힌다.', 139);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (738, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_3.png', '3. 삶아 놓은 닭고기는 결대로 찢는다.', 139);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (739, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_4.png', '4. 양파, 오이와 파프리카는 채썬다', 139);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (740, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_5.png', '5. 적채는 채를 썰어 찬물에 담궈 건져\n물기를 빼 놓는다.', 139);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (741, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00578_6.png', '6. 차갑게 걸러 놓은 닭 육수에 발효된\n겨자와 소금 .설탕, 식초를 넣어 육수\n간을 하고, 그릇에 닭살과 오이 적채,\n파프리카 양파를 올려 차가운 육수를\n넣어 완성한다.', 139);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (742, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_1.png', '1. 감자는 채를 썰어 물에 담궈 전분을\n제거하고, 양파와 당근 파는 채를 썬다.', 140);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (743, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_2.png', '2. 콩나물은 머리와 꼬리를 손질한다', 140);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (744, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_3.png', '3. 물이 끓으면 손질한 콩나물을 넣는다.', 140);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (745, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_4.png', '4. 콩나물이 익으면 체에 건져 찬물에\n헹군다.', 140);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (746, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_5.png', '5. 부추는 약 6cm 길이로 썬다.', 140);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (747, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00579_6.png', '6. 팬에 기름을 두르고 콩나물을 넣고\n살짝 볶다가 썰어 놓은 야채를 넣고\n볶으면서 소금 간을 하고 마지막에\n부추를 넣고', 140);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (748, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_1.png', '1. 비지는 살짝 데쳐 물기를 뺀다.', 141);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (749, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_2.png', '2. 무장아찌는 잘게 다져 비지에 골고루\n섞어 소스를 만든다.', 141);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (750, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_3.png', '3. 토마토는 슬라이스로 썰어 팬에 오일을\n두르고 익힌다.', 141);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (751, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_4.png', '4. 포카치아빵은 반으로 갈라 비지소스를\n바른다.', 141);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (752, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_5.png', '5. 생치즈는 동그랗게 원형으로 찍어,\n비지를 바른 빵에 구운 토마토와 함께\n올리고, 다시 그 위에 비지를 올려\n접시에 담는다.', 141);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (753, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00580_6.png', '6. 루꼴라는 찬물에 담궈 건져 포카치아빵\n옆에 함께 놓는다', 141);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (754, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_1.png', '1. 소고기는 후춧가루, 올리브오일로\n밑간을 한다.', 142);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (755, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_2.png', '2. 더덕은 칼로 껍질을 돌려 깎고,\n파프리카, 아스파라거스와 함께\n가늘게 채썬다.', 142);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (756, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_3.png', '3. 파인애플, 양파, 당근, 대파는 채썬다.', 142);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (757, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_4.png', '4. 밑간한 고기를 팬에 굽는다.', 142);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (758, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_5.png', '5. 따로 갈아 놓은 파인애플과 레몬즙\n꿀을 섞어 드레싱을 만는다', 142);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (759, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00581_6.png', '6. 구워 놓은 고기에 더덕과 파인애플\n아스파라거스 깻잎을 넣어 돌돌 말고,\n팬에 발사믹 소스를 끓인 뒤 말아 놓은\n소고기롤을 넣어 졸인다.', 142);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (760, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_1.png', '1. 새우는 껍질을 벗겨 다진다.', 143);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (761, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_2.png', '2. 오징어는 썰어서 다진다.', 143);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (762, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_3.png', '3. 양파, 샐러리, 마늘은 다져 팬에 볶아\n식힌다.', 143);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (763, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_4.png', '4. 토마토는 껍질을 벗겨 다진다.', 143);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (764, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_5.png', '5. 다진 토마토를 팬에 볶다가 다진\n새우와 오징어, 밥을 넣어 볶고, 3번을\n넣고 볶아 동그랗게 모양을 만든다', 143);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (765, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00582_6.png', '6. 동그란 밥에 레몬을 섞은 달걀노른자를\n묻히고 빵가루 옷을 입혀 팬에 기름을\n둘러 굴려가며 익힌 후 접시에 담고\n어린잎을 올린다.', 143);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (766, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_1.png', '1. 닭고기는 넓게 펴 소금, 후추로 밑간을\n한다.', 144);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (767, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_2.png', '2. 찹쌀은 깨끗이 씻어 밥을 짓는다', 144);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (768, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_3.png', '3. 인삼은 뇌두를 제거 하고 깨끗이 씻어\n길게 썬다.', 144);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (769, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_4.png', '4. 미나리는 데쳐 찬물에 헹구고, 대추는\n씨를 제거하고 채를 썬다.', 144);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (770, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_5.png', '5. 호박잎은 끓는 물에 데쳐 찬물에\n식힌다.', 144);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (771, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00583_6.png', '6. 데친 호박잎에 밑간한 닭고기와\n찹쌀밥을 올리고 그 위에 인삼, 데친\n미나리, 대추를 올려 돌돌 말아 찜통에\n찐 뒤 먹기 좋게 썬다.', 144);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (772, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_1.png', '1. 소고기는 홍두깨살로 준비해서\n0.3mm 두께로 썰고, 배는 껍질을 벗겨\n채 썰어 설탕을 뿌린다.', 145);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (773, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_2.png', '2. 끓는 물에 소금을 넣고 두릅을 데친 후\n찬물에 헹구어 건진다.', 145);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (774, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_3.png', '3. 소금과 설탕, 다진 마늘을 넣고 소고기\n육회 양념장을 만든다.', 145);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (775, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_4.png', '4. 삶은 달걀노른자와 크림치즈, 레몬을\n섞어 짤주머니에 담는다.', 145);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (776, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_5.png', '5. 숙성이 잘된 아보카도는 강판에 갈아\n준비한다.', 145);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (777, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00584_6.png', '6. 접시에 양념한 육회와 두릅을 담고 그\n위에 배를 올리고, 달걀크림과\n아보카도를 짜서 올린다', 145);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (778, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_1.png', '1. 닭은 깨끗이 씻고 포를 뜬다.', 146);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (779, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_2.png', '2. 닭고기는 15분 정도 삶아 식으면 곱게\n다져 다진 마늘, 대파, 저염간장을 넣고\n완자를 만든다.', 146);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (780, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_3.png', '3. 인삼은 뇌두를 제거하고 깨끗이\n씻는다.', 146);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (781, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_4.png', '4. 찹쌀은 약 30분 정도 충분히 불린다', 146);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (782, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_5.png', '5. 대추는 소금물에 깨끗이 씻고 씨를\n제거한다.', 146);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (783, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00585_6.png', '6. 믹서에 인삼과 대추, 불린 찹쌀, 물을\n정도 넣고 곱게 간 뒤, 냄비에 담아 죽을\n쑤다가 다 익으면 만들어 놓은 완자를\n넣고 한소끔 더 끓인다.', 146);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (784, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309015216_1678337536871.jpg', '1. 새우는 내장을 제거하고 껍질을 벗겨 깨끗이 씻는다.', 147);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (785, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021346_1678338826806.jpg', '2. 찜기에 새우와 생강을 넣어 약 5분 정도 찐다.', 147);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (786, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021409_1678338849298.jpg', '3. 마늘은 납작 썰고, 당근과 양파, 파, 깻잎, 청고추, 대추는 굵게 다진다.', 147);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (787, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021426_1678338866582.jpg', '4. 숙주는 꼬리를 떼어 다듬고, 끓는 물에 살짝 데친다.', 147);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (788, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021447_1678338887494.jpg', '5. 팬에 식용유를 두른 뒤 손질한 채소를 넣어 볶는다.', 147);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (789, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/20230309/20230309021505_1678338905796.jpg', '6. 밥과 새우를 넣어 볶고, 저염간장을 넣어 볶아 마무리한다.', 147);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (790, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_1.png', '1. 토마토는 깨끗이 씻어 슬라이스한다.', 148);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (791, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_2.png', '2. 두부는 원형틀을 이용해서 토마토와\n같은 크기로 만들고 소금을 살짝 뿌린\n뒤 물기를 제거한다.', 148);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (792, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_3.png', '3. 팬에 올리브오일을 두르고 두부를 살짝\n굽는다.', 148);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (793, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_4.png', '4. 어린잎은 찬물에 담근다.', 148);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (794, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_5.png', '5. 발사믹소스에 설탕과 레몬즙을 넣고\n골고루 섞어 소스를 만든다.', 148);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (795, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00587_6.png', '6. 두부와 준비한 토마토를 접시에 돌려\n담고 어린잎을 위에 올린 뒤 먹기\n직전에 발사믹소스와 파마산치즈를\n뿌려 완성한다.', 148);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (796, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_1.png', '1. 쌀과 다시마를 넣고 쌀과 동량의\n물을 부어 밥을 고슬고슬하게\n짓는다.', 149);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (797, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_2.png', '2. 양파는 굵게 다지고, 쪽파와 고추는\n송송 썰고, 오징어와 주꾸미는 먹기\n좋은 크기로 썬다.', 149);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (798, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_3.png', '3. 달군 팬에 들기름을 두르고 다진\n마늘을 볶아 향을 낸다.', 149);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (799, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_4.png', '4. 미역을 넣고 볶다가 손질해둔\n오징어, 주꾸미, 새우살, 양파를 넣어\n볶는다.', 149);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (800, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_5.png', '5. 밥을 넣고 소금을 넣어 볶다가\n들깻가루를 넣어 볶는다.', 149);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (801, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00217_6.png', '6. 송송 썬 쪽파와 청양고추, 붉은\n고추를 넣어 조금 더 볶아\n마무리한다.', 149);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (802, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_1.png', '1. 쌀과 현미는 물에 깨끗이 씻어 불린\n후 밥을 고슬고슬하게 짓는다.', 150);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (803, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_2.png', '2. 감자, 고구마, 우엉, 양파, 당근,\n애호박, 연근은 작게 깍둑 썰고,\n마늘은 편 썬다.', 150);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (804, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_3.png', '3. 시금치와 참나물, 깻잎은 굵게 채\n썰고, 미나리는 2.5㎝ 길이로 썬다.', 150);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (805, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_4.png', '4. 물(50g)에 다시마를 우려내 육수를\n내고 청국장을 넣어 잘 푼 뒤\n들깻가루를 넣고 잘 저어주며 졸여\n청국장소스를 만든다.', 150);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (806, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_5.png', '5. 팬에 올리브유와 들기름을 두르고\n양파, 마늘을 약한 불에 볶다가\n감자, 고구마, 우엉, 연근, 당근,\n애호박을 넣고 같이 볶는다.', 150);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (807, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00218_6.png', '6. 밥과 청국장소스를 부어 80% 정도\n볶다가 시금치, 참나물, 미나리를\n넣고 한 번 더 볶고 채 썬 깻잎을\n올려 마무리한다.', 150);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (808, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_1.png', '1. 쌀은 씻어서 체에 건져 1시간 이상\n불린 후 동량의 물을 부어 밥을\n짓는다.', 151);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (809, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_2.png', '2. 아스파라거스, 새송이버섯은 작게\n깍둑 썰고, 무와 우엉, 미나리는\n다진다.', 151);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (810, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_3.png', '3. 식용유(10g)를 두른 팬을 중간 불로\n달궈 달걀을 넣고 스크램블로\n볶는다.', 151);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (811, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_4.png', '4. 문어, 새우살, 주꾸미는 끓는 물에\n살짝 데쳐 작게 자른다.', 151);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (812, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_5.png', '5. 식용유(15g)를 두른 팬을 중간 불로\n달궈 밥을 넣어 볶다가 문어, 새우살,\n주꾸미, 스크램블 에그를 넣고 조금\n더 볶는다.', 151);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (813, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00219_6.png', '6. 무와 우엉에 고추장과 참기름,\n물(40g)을 넣고 묽게 졸인 후 불을\n끄고 미나리를 넣고 섞어\n미나리볶음고추장을 만들어 밥에\n곁들여 마무리한다.', 151);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (814, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_1.png', '1. 감자, 양파, 당근, 피망은 볶음밥에\n넣을 분량은 잘게 다지고, 카레에\n넣을 분량은 한입 크기로 썬다.', 152);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (815, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_2.png', '2. 닭가슴살도 한입 크기로 잘라\n후춧가루로 밑간한다.', 152);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (816, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_3.png', '3. 팬에 식용유(15g)를 두른 뒤 잘게\n다진 감자, 양파, 당근, 피망을 넣고\n볶다가 밥을 넣고 같이 볶는다.', 152);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (817, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_4.png', '4. 식용유(15g)를 두른 팬에 닭고기를\n먼저 볶다가 한입 크기로 썬 감자,\n당근, 양파, 피망, 브로콜리를 넣고\n볶은 뒤 마지막에 방울토마토를\n넣는다.', 152);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (818, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_5.png', '5. 토마토페이스트을 넣고 볶다가\n파프리카가루를 넣어 향을 낸다.', 152);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (819, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00220_6.png', '6. 카레가루를 푼 물(300g)을 부어\n끓이다가 플레인 요구르트를 넣고\n농도를 맞춘 뒤 밥 위에 얹어\n마무리한다.', 152);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (820, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_1.png', '1. 현미로 밥을 지은 뒤 밥 밑간을 넣고\n섞는다.', 153);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (821, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_2.png', '2. 톳에 톳 무침양념을 넣고 버무린다.', 153);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (822, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_3.png', '3. 밥에 톳을 넣고 버무린다.', 153);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (823, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_4.png', '4. 밥을 먹기 좋은 크기로 둥글게 뭉친\n뒤 부순 생김을 골고루 묻힌다.', 153);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (824, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00221_5.png', '5. 주먹밥에 달걀물을 입힌 뒤 팬에\n구워 마무리한다.', 153);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (825, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_1.png', '1. 칵테일새우는 잘게 다진다.', 154);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (826, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_2.png', '2. 다진 쇠고기에 후춧가루와\n참기름(3g)으로 밑간한 후 볶는다.', 154);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (827, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_3.png', '3. 찹쌀현미밥에 참기름(4g)을 넣어\n밑간하고 식힌 후 새우와 쇠고기를\n넣어 뭉쳐준다.', 154);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (828, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_4.png', '4. 마늘과 고추는 볶다가 식초와\n함초가루, 설탕, 물(30g)을 넣고 졸여\n소스를 만든다.', 154);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (829, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_5.png', '5. 밀가루는 반죽한 뒤 얇게 밀어\n삼각형으로 잘라 튀긴 후 기름을\n뺀다.', 154);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (830, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00222_6.png', '6. 위에 주먹밥을 올리고 파인애플을\n올린 후 소스를 곁들여 마무리한다.', 154);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (831, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_1.png', '1. 닭봉은 손질한 뒤 후춧가루를\n섞은 우유에 담가두었다가 구워\n준비한다.', 155);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (832, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_2.png', '2. 단호박, 새송이버섯은 구워서\n준비한다.', 155);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (833, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_3.png', '3. 소스를 만든다.', 155);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (834, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_4.png', '4. 가지, 주키니호박, 당근, 양파,\n시래기는 잘게 다진 뒤 볶는다.', 155);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (835, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_5.png', '5. 볶은 채소와 치즈를 참기름, 간장과\n함께 밥에 넣고 섞는다.', 155);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (836, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00223_6.png', '6. 구운 닭봉을 밥으로 감싼 뒤 구운\n단호박, 새송이버섯, 새싹채소와\n함께 담아 소스를 곁들여\n마무리한다.', 155);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (837, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_1.png', '1. 봄동과 깻잎은 데친 뒤 체에 밭쳐\n물기를 제거한다.', 156);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (838, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_2.png', '2. 데친 꼬막살은 물기를 제거하고\n다져 올리브유로 양념한 밥에\n참깨와 함께 버무린 후 한입 크기로\n주먹밥을 만든다.', 156);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (839, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_3.png', '3. 낙지 다리는 데쳐 찬물에 담가 식힌\n뒤 참기름(3g)에 양념하고, 두릅은\n데쳐 물기를 제거한 뒤 올리브유로\n양념한다.', 156);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (840, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_4.png', '4. 봄동에 꼬막밥을 넣고 만 뒤\n깻잎으로 싸고 데친 미나리로 묶어\n봄주먹밥을 만든다.', 156);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (841, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_5.png', '5. 냄비에 참기름(10g)을 두르고 양파,\n당근, 애호박을 넣고 볶은 뒤 물,\n된장, 고춧가루, 다진 마늘, 붉은\n고추, 청양고추를 넣고 끓이다가\n두부를 넣고 조려 두부강된장을\n만든다.', 156);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (842, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00224_6.png', '6. 봄주먹밥에 낙지와 두릅,\n두부강된장을 곁들여 마무리한다.', 156);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (843, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_1.png', '1. 쌀을 씻어 밥을 지은 뒤 밥 밑간을\n한다.', 157);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (844, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_2.png', '2. 양파, 피클, 삶은 달걀은 다진 뒤\n마요네즈에 섞어\n허브타르타르드레싱을 만든다.', 157);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (845, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_3.png', '3. 김 위에 양쪽으로 밥을 펴고\n허브타르타르드레싱을 위에\n올린 후 만다.', 157);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (846, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_4.png', '4. 깻잎장아찌를 바닥에 깔고 말아놓은\n롤을 올린 후 한 번 더 만다.', 157);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (847, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_5.png', '5. 머스타드소스를 만든다.', 157);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (848, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00225_6.png', '6. 한입 크기로 롤을 썬 뒤 새싹과\n소스를 곁들여 마무리한다.', 157);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (849, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_1.png', '1. 쌀을 물에 불린 뒤 완두콩을 함께\n넣고 밥을 짓는다.', 158);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (850, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_2.png', '2. 달걀을 풀어 체에 내린 뒤 지단을\n부치면서 반 정도 익었을 때 그 위에\n얇게 썬 주키니호박, 당근, 가지를\n올려 익힌다.', 158);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (851, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_3.png', '3. 참치를 참치밑간에 재운 뒤 겉만\n살짝 익힌다.', 158);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (852, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_4.png', '4. 김에 밥을 올리고, 뒤집어 참치를\n올려 만 뒤 그 위에 지단을 올려\n한 번 더 만다.', 158);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (853, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_5.png', '5. 사과드레싱을 만들고,\n토마토소스 재료를 간 뒤 체에\n거른다.', 158);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (854, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00226_6.png', '6. 참치비빔밥롤을 담은 뒤\n토마토소스를 곁들이고, 케일에\n사과드레싱을 뿌려 마무리한다.', 158);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (855, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_1.png', '1. 소스를 만든다.', 159);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (856, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_2.png', '2. 파프리카와 파인애플을 먹기 좋게\n썬다.', 159);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (857, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_3.png', '3. 스크램블 에그를 만든다.', 159);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (858, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_4.png', '4. 김발 위에 ⅔크기로 자른 김을 놓고\n밥을 고루 편 뒤 새싹채소,\n스크램블 에그, 파프리카, 기름 뺀\n참치, 파인애플을 올리고 소스를\n고르게 뿌린다.', 159);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (859, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00227_5.png', '5. 반으로 접어 먹기 좋게 썰어\n마무리한다.', 159);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (860, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_1.png', '1. 당근, 피망, 양파를 잘게 다진 뒤\n기름에 살짝 볶아 밥에\n마요네즈(10g)와 함께 넣어 섞는다.', 160);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (861, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_2.png', '2. 통조림 연어는 기름기를 빼고,\n식초를 넣은 끓는 물에서 30초 정도\n데친다.', 160);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (862, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_3.png', '3. 데리야키소스와 마요네즈(10g)에\n연어를 넣고 버무려 양념한다.', 160);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (863, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_4.png', '4. 밥을 동그랗게 빚은 뒤 가운데에\n연어를 넣고 주먹밥을 만든다.', 160);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (864, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_5.png', '5. 달걀물과 빵가루를 입혀 160℃로\n예열한 기름에 바삭하게 튀긴다.', 160);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (865, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00228_6.png', '6. 연어주먹밥튀김을 담고\n어린잎채소를 곁들인 뒤\n오리엔탈소스를 곁들여 마무리한다.', 160);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (866, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_1.png', '1. 오징어 몸통 안쪽에 칼집을 내고\n끓는 물에 데친다.', 161);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (867, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_2.png', '2. 파프리카와 당근, 피망은 작게 다져\n볶은 뒤 밥에 넣어 섞는다.', 161);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (868, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_3.png', '3. 쇠고기는 참기름에 볶는다.', 161);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (869, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_4.png', '4. 달걀을 풀어 약한 불로 달군 팬에\n부은 뒤 김가루를 뿌려 지단을\n부치고, 바닥이 익으면 깻잎과\n밥을 올린다.', 161);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (870, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_5.png', '5. 데친 오징어, 볶은 쇠고기 순으로\n올려 만 뒤 랩으로 싼다.', 161);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (871, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00229_6.png', '6. 고추장과 매실청을 섞은 뒤 팬에\n넣고 약한 불에서 끓여 소스를\n만들고 한입 크기로 썬 김밥에\n구운 버섯을 함께 곁들여\n마무리한다.', 161);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (872, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_1.png', '1. 닭고기 삶는 재료를 물(100g)에 넣어\n끓이다가 닭가슴살을 넣고 삶아\n한입 크기로 썬다.', 162);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (873, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_2.png', '2. 양배추는 곱게 채 썰고, 청포묵은\n먹기 좋은 크기로 썬다.', 162);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (874, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_3.png', '3. 숙주는 끓는 물에 데쳐 물기를 뺀다', 162);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (875, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_4.png', '4. 비빔소스를 만든다.', 162);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (876, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_5.png', '5. 쌀에 물을 붓고 밥을 짓는다.', 162);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (877, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00230_6.png', '6. 밥 위에 닭가슴살, 양배추, 청포묵,\n숙주, 달걀지단, 무순을 담아\n마무리한다.', 162);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (878, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_01.png', '1. 조미된 족발은 뜨거운 맥주에 데쳐내 기름기를\n제거한다.', 163);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (879, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_02.png', '2. 고구마는 편으로 썰어 냉수에 헹궈 기름\n없는 팬에 구워준다.', 163);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (880, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_03.png', '3. 가지는 편으로 썰어서 수분을 제거한다.', 163);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (881, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_04.png', '4. 가지는 편으로 썰어 살짝 구워준다.', 163);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (882, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00325_05.png', '5. 백김치는 물에 헹궈서 손으로 꼭 짠 후 송송\n썰어서 양념을 해 무쳐준다.', 163);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (883, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/10_00325_1.png', '6. 무쳐준 김치와 함께 구워진 채소와 족발수육을\n담아 완성한다.', 163);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (884, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_01.png', '1. 함초는 송송 다져준다.', 164);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (885, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_02.png', '2. 당근, 새송이, 샐러리, 양파를 송송 다져준다.', 164);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (886, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_03.png', '3. 볼에 소고기, 돼지고기, 다져 놓은 채소와\n다진마늘, 다진대파, 참기름을 넣고 반죽해준다.', 164);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (887, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_04.png', '4. 여기에 함초 다진 것을 넣고 다시 반죽해 준다.', 164);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (888, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_05.png', '5. 떡갈비 모양으로 만들어준 후 팬에 노릇하게\n구워준다.', 164);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (889, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00326_06.png', '6. 소스를 만들어 구워진 떡갈비를 졸여준다.', 164);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (890, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_01.png', '1. 모시조개는 볶은소금을 넣어서 해감시킨다.', 165);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (891, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_02.png', '2. 준비된 다시마물에 준비된 채소의 국물재료를\n넣고 국물을 만들어준다.', 165);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (892, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_03.png', '3. 만들어진 국물에 모시조개를 넣고 청주를\n부어서 국물을 만들어준다.', 165);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (893, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_04.png', '4. 쪽파는 송송 다지고 마늘은 곱게 다져주고\n청홍고추는 가늘게 채 썰어준다.', 165);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (894, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_05.png', '5. 면을 각각 삶아준다.', 165);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (895, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00327_06.png', '6. 삼색 면을 그릇에 담고 만들어진 국물을\n부워서 완성한다.', 165);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (896, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_01.png', '1. 당근, 청경채, 배추, 애호박, 팽이버섯은 먹기\n좋은 크기로 잘라준다.', 166);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (897, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_02.png', '2. 흑돼지고기, 저염된장, 후추를 넣어서 불순물을\n제거하면서 고기를 삶아준다.', 166);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (898, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_03.png', '3. 천연다시다로 육수를 만들어 체에 걸러준다.', 166);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (899, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_04.png', '4. 손질한 야채들과 청양고추를 잘라 함께 육수에\n데쳐 국물에 매운 맛을 낸다.', 166);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (900, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_05.png', '5. 곁들이장을 만들어 준다.', 166);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (901, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00328_06.png', '6. 냄비에 데쳐준 채소와 국물을 붓고 고기를\n먹기좋게 썰어 소스랑 완성한다.', 166);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (902, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_01.png', '1. 바지락은 해감을 시켜준 후 물에 넣어 바지락\n육수를 만들어준다.', 167);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (903, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_02.png', '2. 바지락살은 따로 떼어내어 다진소고기와\n다시 다져서 양념해 볶아준다.', 167);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (904, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_03.png', '3. 두부는 물기제거를 한 후 함초, 깻잎, 다진양파,\n를 넣어 치대어 두부볶음을 만들어준다.', 167);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (905, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_04.png', '4. 애호박은 채 썰어주고 양파, 오이, 당근, 표고도\n채 썰어 각각 볶아준다.', 167);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (906, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_05.png', '5. 볶은 바지락과 쌀을 넣어 밥을 한다.', 167);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (907, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00329_06.png', '6. 몰드에 밥과 각종 채소들을 올려주고 마지막에\n두부 스크램블을 올려 밥을 완성한다.', 167);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (908, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_01.png', '1. 뜨거운 물에 파스타면을 삶아낸 뒤 물기를\n빼준다.', 168);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (909, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_02.png', '2. 송송 썬 청양고추를 생들기름에 볶아주다\n면을 볶는다.', 168);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (910, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_03.png', '3. 볶아준 청양고추에 삶아진 면을 볶아준다.', 168);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (911, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_04.png', '4. 열무김치는 송송 썰어서 들기름에 살짝\n볶아준다.', 168);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (912, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_05.png', '5. 오이는 돌려깎기 해서 채 썰어주고 청고추,\n2가지색 파프리카, 당근도 채 썰어 각각 볶아\n준다.', 168);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (913, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00330_06.png', '6. 청양고추에 볶아진 파스타면을 볶은 채소와\n한 번 더 볶아 완성한다.', 168);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (914, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_01.png', '1. 갈비는 핏물을 빼주고 뜨거운 물에 80% 이상\n데쳐준 뒤 갈비와 감자를 물을 자작하게 넣고\n청주, 생강청을 넣고 푹 삶아준다.', 169);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (915, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_02.png', '2. 양파, 청고추, 홍고추는 먹기 좋게 썰어준다.', 169);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (916, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_03.png', '3. 마른미역줄기는 냉수에 행군 뒤 물기를 빼준다.', 169);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (917, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_04.png', '4. 양념장을 바글바글 끓여준다.', 169);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (918, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_05.png', '5. 끓고 있는 양념장에 삶아진 갈비, 감자, 당근\n순으로 끓이다 메추리알을 넣고 졸여준다.', 169);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (919, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00331_06.png', '6. 마지막에 미역줄기를 넣고 볶아주는 형식으로\n갈비찜을 완성한다.', 169);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (920, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_01.png', '1. 곤드레는 뜨거운 물에 데쳐낸 뒤 깨끗하게\n헹구고 송송 썰어준다.', 170);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (921, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_02.png', '2. 두부의 물기를 짜 준다.', 170);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (922, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_03.png', '3. 양념장 재료와 물기를 짠 두부로 양념장을\n만들어준다.', 170);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (923, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_04.png', '4. 느타리버섯은 뜨거운 물에 데쳐낸 후 수분을\n빼 가늘게 찢어준다.', 170);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (924, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_05.png', '5. 냄비에 참기름을 두르고 곤드레를 볶다가 쌀을\n넣고 밥을 해준다.', 170);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (925, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00332_06.png', '6. 뜸을 들일 때 느타리버섯을 올려주어 밥을\n완성한다.', 170);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (926, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_01.png', '1. 찹쌀은 불려주고 전복은 손질해 내장과 분리해\n준 뒤 청주를 넣어서 전복 내장을 믹서에\n갈아준다.', 171);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (927, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_02.png', '2. 전복 몸에 칼집을 내준 후 참기름을 두른 팬에\n구워준다.', 171);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (928, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_03.png', '3. 당근과 미역은 송송 잘라준다.', 171);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (929, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_04.png', '4. 전복내장을 먼저 참기름에 충분히 볶아준다.', 171);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (930, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_05.png', '5. 내장을 볶아주다가 불린 찹쌀을 넣고 충분히\n볶아준다.', 171);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (931, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00333_06.png', '6. 죽이 완성될 때 당근, 미역을 넣고 죽을 끓여\n주고 전복은 고명으로 올려준다.', 171);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (932, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_01.png', '1. 주꾸미는 세척해 물기를 빼준다.', 172);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (933, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_02.png', '2. 무와 단호박은 먹기 좋게 썰어준다.', 172);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (934, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_03.png', '3. 청고추, 미나리, 양파를 먹기 좋게 썰어준다.', 172);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (935, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_04.png', '4. 청양 고추를 어슷하게 썰고 마늘은 편으로 썰어\n국물을 끓여준다.', 172);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (936, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_05.png', '5. 국물이 만들어지면 무, 단호박을 넣고 끓여준다.', 172);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (937, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00334_06.png', '6. 국물에 대파를 넣어주고 모든 재료를 넣어\n완성한다.', 172);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (938, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_01.png', '1. 파프라카, 양파, 홍고추는 가늘게 채 썰어준다.', 173);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (939, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_02.png', '2. 함초는 세척 후 먹기 좋게 손질해 준다.', 173);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (940, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_03.png', '3. 무순을 물 속에 담가준다.', 173);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (941, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_04.png', '4. 편으로 썰어준 소고기에 채 썰어준 채소와\n함초를 꼭 싸 말아준다.', 173);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (942, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_05.png', '5. 마늘은 편으 로 썰 어 마늘기름에 한 번\n구워준다.', 173);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (943, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00335_06.png', '6. 구운 마늘을 깔고 말아준 고기를 굽는다.', 173);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (944, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_01.png', '1. 닭가슴살을 이용해 국물을 만든다.', 174);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (945, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_02.png', '2. 닭가슴살은 먹기 좋게 찢어 흰 후추, 버섯마늘\n소금으로 밑간을 해준다.', 174);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (946, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_03.png', '3. 콩나물은 머리를 떼어내고 데쳐준다.', 174);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (947, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_04.png', '4. 계란은 황백지단으로 부친 후 채 썰어준다.', 174);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (948, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_05.png', '5. 3종류의 묵은 먹기 좋은 크기로 썰어준다.', 174);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (949, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00336_06.png', '6. 그릇에 밥을 담고 콩나물을 올리고 3종류의\n묵을 올린 후 계란으로 고명해 완성한다.', 174);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (950, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_01.png', '1. 쌀을 깨끗하게 씻은 후에 쌀뜨물은 따로\n보관하고 불려준 쌀로 밥을 해주고 접시에\n펼쳐 식혀준다.', 175);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (951, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_02.png', '2. 우엉과 연근, 감자는 송송 다져준 후 아린 맛을\n빼기위해 물에 담가준다.', 175);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (952, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_03.png', '3. 우엉과 연근, 감자는 물기를 빼고 볶아준다.', 175);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (953, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_04.png', '4. 청고추, 양파, 당근은 송송 다져서 함께\n볶아준 뒤, 함초 소금을 이용해 1차 간을\n해준다.', 175);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (954, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_05.png', '5. 그 릇 에 모 든 재 료 를 넣 고 오 니 기 리 를\n만들어준다.', 175);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (955, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00337_06.png', '6. 그릇에 담아 완성한다.', 175);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (956, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_01.png', '1. 단 호박은 속을 파 준다.', 176);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (957, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_02.png', '2. 찹쌀은 물에 불려준다.', 176);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (958, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_03.png', '3. 밤은 먹기 좋게 잘라서 물에 담가준다.', 176);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (959, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_04.png', '4. 대추는 씨를 제거하고 먹기 좋게 잘라준다.', 176);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (960, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_05.png', '5. 양념장을 만들어 먼저 쌀과 밤을 재워준 후\n찰밥을 만든다.', 176);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (961, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00338_06.png', '6. 단호박 속에 찰밥을 넣어서 찜기에 쪄준다.', 176);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (962, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_01.png', '1. 망고는 손질 후 버터로 살짝 볶고 레드와인을\n넣고 졸이다가 믹서로 갈아주고 딸기잼으로\n간과 농도를 맞추어 레드와인 소스를 만든다.', 177);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (963, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_02.png', '2. 소 채끝살은 손질 후 만들어진 레드와인 소스에\n마리네이드 해준다.', 177);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (964, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_03.png', '3. 감자는 껍질을 제거 후 먹기 좋은 크기로 잘라서\n뜨거운 물에 데쳐내 버터에 볶아준다. 당근은\n먹기좋은 크기로 잘라 뜨거운 물에 데쳐 버터\n에 볶아주고, 시금치도 데친 후 수분을 제거하\n고 버터에 볶는다.', 177);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (965, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_04.png', '4. 마리네이드 한 고기를 구워준다.', 177);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (966, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_05.png', '5. 마리네이드 하고 남은 망고소스를 다시 팬에\n졸여 소스를 만든다.', 177);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (967, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00339_06.png', '6. 접시에 준비된 채소와 안심을 담고 소스를\n뿌려 완성한다.', 177);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (968, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_1.png', '1. 식빵은 가장 자리를 잘라 밀대를 이용\n하여 얇게 밀어 준비한다.', 178);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (969, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_2.png', '2. 감자는 삶은 뒤 곱게 으깨어 올리고당을\n넣고 골고루 섞는다.', 178);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (970, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_3.png', '3. 달걀은 약 13분 정도 삶아 껍질을\n벗기고, 반으로 갈라 썰어놓는다.', 178);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (971, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_4.png', '4. 당근과 양파 오이도 얇게 썰어놓는다.', 178);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (972, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_5.png', '5. 토마토는 포를 떠서 준비한다.', 178);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (973, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00441_6.png', '6. 으깬 감자(②)에 준비한 달걀(③)과\n채소(④)를 넣고 소금을 약간 넣은 뒤\n골고루 섞어, 밀어 놓은 식빵에 토마토,\n치즈, 양상추와 함께 넣어 돌돌 말아\n완성한다.', 178);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (974, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_1.png', '1. 황태는 가위로 약 3cm 길이로 잘라\n스프레이로 물기를 준다.', 179);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (975, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_2.png', '2. 밀가루를 세 군데로 나눠 각각 찹쌀\n가루와 녹말가루, 삼색 가루를 넣어\n세 가지의 색을 만든다.', 179);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (976, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_3.png', '3. 만들어 놓은 밀가루에 달걀을 넣어\n골고루 섞는다.', 179);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (977, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_4.png', '4. 튀김 반죽(③)에 황태(①)를 넣어\n섞은 후, 튀김 기름 170℃ 의 온도에서\n약 1분 정도 튀겨 기름을 빼 놓는다.', 179);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (978, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_5.png', '5. 파프리카는 속씨를 제거하고, 양파와\n당근, 오이, 파인애플과 함께 썰어 준비\n한다.', 179);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (979, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00442_6.png', '6. 저염간장에 설탕과 식초를 넣어 골고루\n섞은 후 냄비에서 바글바글 끓이다가\n물녹말을 넣고, 썰어놓은 채소를 넣어\n한 번 더 끓인 후 황태 탕수육(④)에\n곁들여 완성한다.', 179);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (980, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_1.png', '1. 소시지는 먹기 좋은 크기로 썰어 나무\n막대기에 꽂아놓는다.', 180);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (981, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_2.png', '2. 파프리카는 작게 다져 준비한다.', 180);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (982, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_3.png', '3. 으깬 감자를 준비된 밥과 함께 골고루\n섞는다.', 180);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (983, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_4.png', '4. 으깬 감자를 다시 준비된 밥과 함께\n소금을 조금 넣고 골고루 섞는다.', 180);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (984, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_5.png', '5. 밥과 감자가 잘 섞어지면, 다시 다져진\n파프리카를 넣고 섞어준다.', 180);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (985, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00443_6.png', '6. 막대기에 밀가루를 살짝 바르고, 소시\n지에 반죽(⑤)을 감싸듯 손으로 둥글게\n말아 밀가루와, 달걀물, 빵가루 순서로\n입혀 튀김 기름 170℃의 온도에서\n약 2분 정도 튀긴 후 기름을 제거하여\n완성한다.', 180);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (986, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_1.png', '1. 소등심은 소금과 후춧가루, 로즈마리를\n뿌려 약 30분 이상 숙성을 시킨다.', 181);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (987, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_2.png', '2. 애호박은 동그랗게 자르고, 양파는 형태\n그대로 떼어내고, 가지는 직사각형으로\n잘라 준비한다.', 181);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (988, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_3.png', '3. 파프리카는 속을 파내고 작은 사각으로\n자르고, 토마토는 씨를 제거하고\n네모지게 잘라 준비해 놓는다.', 181);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (989, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_4.png', '4. 사과는 껍질을 깨끗이 씻어 길쭉하게\n썰어놓는다.', 181);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (990, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_5.png', '5. 발사믹소스에 설탕과 마늘을 넣고 썰어\n놓은 사과채를 넣어 잘 섞은 후 바글바글\n끓여 소스를 만들어놓는다.', 181);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (991, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00444_6.png', '6. 팬이 따끈해지면, 호박과 양파, 가지를\n구워 내고, 팬에 소등심을 앞뒤로 구워\n먹기 좋은 크기로 자른 뒤 구워 놓은\n양파와 호박, 가지에 각각 올리고,\n끓여놓은 소스와 파프리카, 토마토를\n얹어 완성한다.', 181);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (992, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_1.png', '1. 당근은 채를 곱게 썰어 준비한다.', 182);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (993, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_2.png', '2. 달걀은 노란자와 흰자로 나눠 각각\n지단을 부쳐 약 5cm 길이로 채를\n썰어놓는다.', 182);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (994, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_3.png', '3. 시금치는 끓는 물에 소금을 넣고 파랗게\n데쳐 물기를 제거하고 무쳐놓는다.', 182);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (995, 4, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_4.png', '4. 돼지고기는 마늘, 파, 설탕, 맛술, 간장,\n후춧가루, 통깨, 참기름을 넣고 30분간\n숙성시킨 뒤 팬에 볶아서 준비한다.', 182);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (996, 5, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_5.png', '5. 고구마는 삶아 으깨고, 두부는 소창에\n물기를 짜고, 토마토와 양파, 견과류는\n입자있게 다져섞는다.', 182);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (997, 6, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00445_6.png', '6. 라이스 페이퍼를 따뜻한 물에 담궜\n다가 바로 꺼내어 으깬 고구마(⑤)를\n먼저 깔고, 그 위에 준비한 재료(②,\n③, ④)를 올려 돌돌 말은 뒤 먹기 좋은\n크기로 썰어 완성한다.', 182);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (998, 1, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_1.png', '1. 밀가루를 세 개로 나눠 호박가루와\n백년초가루, 시금치가루를 각각 넣고\n물, 식용유, 소금을 넣어 반죽 한 후\n비닐봉지에 담아 1~2시간 정도 숙성\n시킨다.', 183);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (999, 2, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_2.png', '2. 양파, 마늘, 버섯들은 입자있게 다져\n준비한다.', 183);
INSERT INTO `recipe_manual` (`recipe_manual_id`, `recipe_manual_step`, `manual_menu_image`, `manual_content`, `recipe_id`) VALUES (1000, 3, 'http://www.foodsafetykorea.go.kr/uploadimg/cook/20_00446_3.png', '3. 두부는 소창을 이용하여 물기를 짜낸다.', 183);
